n	float: none;\n}\n\n.navigation_wrap .searchform {\n	position: absolute;\n	right: 3px;\n	bottom: 2px;\n}\n\n\n\n// BUTTON INPUTS\n// -------------------\ninput[type=\"submit\"],\n.submit,\ninput[type=\"button\"],\n.button,\ninput[type=\"reset\"],\n.reset{\n	padding: 4px 9px;\n	cursor: pointer;\n	text-decoration: none;\n	.box-sizing(content-box);\n	.border-radius(2px);\n	color:#000000;\n	text-shadow:0 1px 0 rgba(255,255,255,0.5);\n	border:1px solid #CCC;\n	#gradient > .vertical(#ffffff, #EEE);\n	box-shadow: inset 1px 1px 0px #FFF;\n	font-family: @buttonFontFamily;\n\n	&:hover{\n		color:#000000;\n		text-decoration: none;\n		text-shadow:0 1px 0 rgba(255,255,255,0.5);\n		border:1px solid #aeaeae;\n		#gradient > .vertical(#f3f3f3, #cfcfcf);\n	}\n	&:active{\n		color:#000000;\n		text-shadow:0 1px 0 rgba(255,255,255,0.5);\n		border:1px solid #8e8e8e;\n		#gradient > .vertical(#afafaf, #d3d3d3);\n		box-shadow: none;\n	}\n}\n\ninput[type=\"image\"]{\n	box-shadow: none;\n}\n\n\n//\n// Breadcrumbs\n// --------------------------------------------------\n\n\n.breadcrumb {\n  padding: 8px 15px;\n  margin: 0 0 @baseLineHeight;\n  list-style: none;\n  background-color: #f5f5f5;\n  .border-radius(4px);\n  li {\n    display: inline-block;\n    .ie7-inline-block();\n    text-shadow: 0 1px 0 @white;\n  }\n  .divider {\n    padding: 0 5px;\n    color: #ccc;\n  }\n  .active {\n    color: @grayLight;\n  }\n}\n//\n// Close icons\n// --------------------------------------------------\n\n\n.close {\n  float: right;\n  font-size: 20px;\n  font-weight: bold;\n  line-height: @baseLineHeight;\n  color: @black;\n  text-shadow: 0 1px 0 rgba(255,255,255,1);\n  .opacity(20);\n  &:hover {\n    color: @black;\n    text-decoration: none;\n    cursor: pointer;\n    .opacity(40);\n  }\n}\n\n// Additional properties for button version\n// iOS requires the button element instead of an anchor tag.\n// If you want the anchor version, it requires `href=\"#\"`.\nbutton.close {\n  padding: 0;\n  cursor: pointer;\n  background: transparent;\n  border: 0;\n  -webkit-appearance: none;\n}// --------------------------------------------------\n// PageLines Pager\n// --------------------------------------------------\n\n\n.pager {\n  list-style: none;\n  text-align: center;\n  .clearfix();\n	li {\n	  display: inline;\n	}\n	a,\n	span {\n		display: inline-block;\n		padding: 5px 14px;\n		.box-shadow(~\"1px 1px 0 @baseLighterMore, inset 1px 1px 0 @baseLighterMore\");\n		.buttonBackground(@baseContrastSlight, darken(@baseContrastSlight, 3));\n		border: 1px solid @baseContrastDark;\n		.border-radius(15px);\n	}\n	a:hover {\n	  text-decoration: none;\n	  background-color: @baseContrastMore;\n	}\n	a:active{\n		.box-shadow(~\"1px 1px 0 @baseLighterMore, inset 1px 1px 0 rgba(0,0,0,.2)\");\n		background-color: @baseContrastDark;\n		border: 1px solid @baseContrastDarkMore;\n	}\n	.next{\n		a, span{\n			float: right;\n		}\n	}\n	.previous a {\n	  	float: left;\n	}\n	.disabled {\n		a, a:hover, span {\n		  color: @baseContrast;\n		  background-color: @textColorSubtle;\n		  cursor: default;\n		}\n	}\n}\n//\n// Pagination (multiple pages)\n// --------------------------------------------------\n\n.pagination {\n	.wp-pagenavi{\n		// Reset\n		span.pages{\n			.noteText();\n			float: left;\n			line-height: (@baseLineHeight * 1.5) - 2;\n		}\n		a, span, a:hover, span.current {\n			text-decoration: none;\n			border: none;\n			padding: 0;\n			margin: 0;\n			font-weight: normal;\n		}\n		a, span.current{\n			.box-shadow(0 1px 2px rgba(0,0,0,.05));\n		}\n	}\n	ul{\n		.box-shadow(0 1px 2px rgba(0,0,0,.05));\n		.border-radius(3px);\n	}\n	ul, &.pagenavi {\n		display: inline-block;\n		.ie7-inline-block();\n		margin: 0;\n		> a span.pg,\n		> span.pg,\n		.wp-pagenavi a,\n		.wp-pagenavi span.current,\n		.wp-pagenavi span.extend{\n			float: left;\n			padding: 0 14px;\n			line-height: (@baseLineHeight * 1.5) - 2;\n			text-decoration: none;\n			background-color: @pl-base;\n			border: 1px solid @baseContrast;\n			border-left-width: 0;\n			color: @textColorSubtle;\n		}\n		> span.pg,\n		> a:hover span.pg,\n		.wp-pagenavi a:hover,\n		.wp-pagenavi span.current{\n			box-shadow: inset 1px 1px 0 @baseLighterMore;\n			background-color: @baseContrastSlight;\n			color: @pl-text;\n			text-decoration: none;\n		}\n		> li{\n		  display: inline;\n			> a,\n			> span{\n				float: left;\n				padding: 0 14px;\n				line-height: (@baseLineHeight * 1.5) - 2;\n				text-decoration: none;\n				background-color: @pl-base;\n				border: 1px solid @baseContrast;\n				border-left-width: 0;\n				color: @textColorAlt;\n			}\n			&.active > a,\n			&.active > span,\n			> a:hover{\n				background-color: @baseContrast;\n				text-decoration: none;\n			}\n		}\n		> .active a,\n		> .active span{\n			color: @textColorAlt;\n			cursor: default;\n		}\n		> li:first-child > a,\n		li:first-child > span,\n		> a:first-child > span,\n		> span:first-child,\n		.wp-pagenavi > span.pages + a,\n		.wp-pagenavi > span.pages + span.current{\n			border-left-width: 1px;\n			.border-radius(3px 0 0 3px);\n		}\n		> li:last-child > a,\n		li:last-child > span,\n		> a:last-child > span,\n		> span:last-child,\n		.wp-pagenavi a:last-child,\n		.wp-pagenavi span:last-child{\n			.border-radius(0 3px 3px 0);\n		}\n		> .disabled > span,\n		.disabled > a,\n		.disabled > a:hover {\n				opacity: .7;\n			  background-color: transparent;\n			  cursor: default;\n		}\n	}\n	.desc, .wp-pagenavi span.pages {\n		.noteText();\n		line-height: (@baseLineHeight * 1.5) - 2;\n\n		@media (max-width: 767px) { display: none;}\n	}\n}\n\n// Centered\n.pagination-centered {\n  text-align: center;\n}\n.pagination-right {\n  text-align: right;\n}\n//\n// Progress bars\n// --------------------------------------------------\n\n\n// ANIMATIONS\n// ----------\n\n// Webkit\n@-webkit-keyframes progress-bar-stripes {\n  from  { background-position: 40px 0; }\n  to    { background-position: 0 0; }\n}\n\n// Firefox\n@-moz-keyframes progress-bar-stripes {\n  from  { background-position: 40px 0; }\n  to    { background-position: 0 0; }\n}\n\n// IE9\n@-ms-keyframes progress-bar-stripes {\n  from  { background-position: 40px 0; }\n  to    { background-position: 0 0; }\n}\n\n// Opera\n@-o-keyframes progress-bar-stripes {\n  from  { background-position: 0 0; }\n  to    { background-position: 40px 0; }\n}\n\n// Spec\n@keyframes progress-bar-stripes {\n  from  { background-position: 40px 0; }\n  to    { background-position: 0 0; }\n}\n\n\n\n// THE BARS\n// --------\n\n// Outer container\n.progress {\n  overflow: hidden;\n  height: @baseLineHeight;\n  margin-bottom: @baseLineHeight;\n  #gradient > .vertical(#f5f5f5, #f9f9f9);\n  .box-shadow(inset 0 1px 2px rgba(0,0,0,.1));\n  .border-radius(@baseBorderRadius);\n}\n\n// Bar of progress\n.progress .bar {\n  width: 0%;\n  height: 100%;\n  color: @white;\n  float: left;\n  font-size: 12px;\n  text-align: center;\n  text-shadow: 0 -1px 0 rgba(0,0,0,.25);\n  #gradient > .vertical(#149bdf, #0480be);\n  .box-shadow(inset 0 -1px 0 rgba(0,0,0,.15));\n  .box-sizing(border-box);\n  .transition(width .6s ease);\n}\n.progress .bar + .bar {\n  .box-shadow(~\"inset 1px 0 0 rgba(0,0,0,.15), inset 0 -1px 0 rgba(0,0,0,.15)\");\n}\n\n// Striped bars\n.progress-striped .bar {\n  #gradient > .striped(#149bdf);\n  .background-size(40px 40px);\n}\n\n// Call animation for the active one\n.progress.active .bar {\n  -webkit-animation: progress-bar-stripes 2s linear infinite;\n     -moz-animation: progress-bar-stripes 2s linear infinite;\n      -ms-animation: progress-bar-stripes 2s linear infinite;\n       -o-animation: progress-bar-stripes 2s linear infinite;\n          animation: progress-bar-stripes 2s linear infinite;\n}\n\n\n\n// COLORS\n// ------\n\n// Danger (red)\n.progress-danger .bar, .progress .bar-danger {\n  #gradient > .vertical(#ee5f5b, #c43c35);\n}\n.progress-danger.progress-striped .bar, .progress-striped .bar-danger {\n  #gradient > .striped(#ee5f5b);\n}\n\n// Success (green)\n.progress-success .bar, .progress .bar-suc