INSERT INTO `wp_options` VALUES (23601,'_transient_plapi_draft_core_raw','a:2:{s:8:\"sections\";s:39539:\"\n.pl-watermark{\n	text-align: center; \n	.stack-element{\n		margin-bottom: 8px;\n	}\n	\n	.fb-like-box span{ display: block; line-height:0; padding:0; }\n	\n	.powered-by{\n		text-align: center;\n	\n		font-size: 10px;\n	}\n	\n	.the-watermark {\n		text-align: center;\n		padding: 5px;\n		a{\n			opacity: .7;\n			&:hover{\n				opacity: 1;\n			}\n			img{\n				max-width: 45px;\n			}\n		}\n	}\n}\n\n	\n\n\n.section-textbox {\n\n	.pl-section-pad{\n		padding: 0;\n		.textbox-wrap{\n			padding: 15px 0;\n		}\n		.textbox-wrap p:last-child{\n			margin-bottom: 0;\n		}\n		.textbox-wrap p:first-child{\n			margin-top: 0;\n		}\n	}\n}\n/* PAGELINES STYLES */\n .revslider-container{\n	width:100% !important;\n	position:relative;\n	padding:0;\n	max-height:480px !important;\n	overflow:hidden;\n 	.tp-thumbs {\n		padding:3px;\n	}\n\n	.text-center{\n		text-align: center;\n\n	}\n	/* Text Sizing*/\n	.slider-content{\n		font-family: @plHeaderFont;\n		text-shadow: 0 -1px 0 rgba(0,0,0,.7);\n		font-size: 34px;\n		line-height: 57px;\n		&.left-side,\n		&.right-side{\n			max-width: 400px;\n			text-align: left;\n		}\n		&.centered{\n			text-align: center;\n		}\n\n		h2{\n			font-size: inherit;\n			line-height: inherit;\n			.slider-text {\n				padding: 4px 0px;\n				background: rgba(0, 0, 0, 0.5);\n				box-shadow: 10px 0 0 rgba(0, 0, 0, 0.5), -10px 0 0 rgba(0, 0, 0, 0.5);\n				color: #fff;\n\n				margin-bottom: 1px;\n				background-position: right -53px;\n				.transition(all 0.3s linear);\n				margin-bottom: 15px;\n			}\n		}\n		a.slider-btn {\n			font-size: 13px;\n			line-height: 1.3em;\n			color: #eee;\n			background: rgba(0, 0, 0, 0.5);\n			left: -10px;\n			top: 10px;\n			position: relative;\n			padding: 10px 18px;\n			.transition(all 0.3s linear);\n			&:hover{\n				color:#fff;\n				background: @linkColor;\n			}\n		}\n		&.slider-black-text{\n			.slider-text{\n				background: rgba(255, 255, 255, 0.7);\n				box-shadow: 10px 0 0 rgba(255, 255, 255, 0.7), -10px 0 0 rgba(255, 255, 255, 0.7);\n				text-shadow: 0 1px 0 #fff;\n				color:#000;\n			}\n			a.slider-btn{\n				background: #fff;\n				color: #000;\n					text-shadow: 0 1px 0 #fff;\n				opacity: .6;\n				&:hover{\n					opacity: 1;\n				}\n			}\n		}\n	}\n\n\n\n\n}\n\n\n/* SLIDER STYLES */\n\n.header-shadow{\n	background:  url(\"@{plRoot}/sections/revslider/images/header-shadow.png\") no-repeat center top;\n	background-size: 100% auto;\n	width: 100%;\n	height: 30px;\n	position: absolute;\n	top: 0;\n	left: 0;\n	z-index: 27;\n}\n\n.tp-hide-revslider,\n.tp-caption.tp-hidden-caption{\n	visibility:hidden !important;\n	display:none !important;\n}\n\n.tp-caption {\n	z-index:1;\n	&.boxshadow{\n		box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);\n	}\n	&.noshadow {\n		text-shadow: none;\n	}\n	a {\n		.transition(all 0.2s ease-out);\n	}\n}\n\n.tp_inner_padding{\n	.box-sizing( border-box );\n	max-height:none !important;\n}\n\n\n/****************************************************************\n\n	-	SET THE ANIMATION EVEN MORE SMOOTHER ON ANDROID   -\n\n******************************************************************/\n.tp-simpleresponsive{\n	.slotholder *,\n	.tp-simpleresponsive img{\n		-webkit-transform: translateZ(0);\n		-webkit-backface-visibility: hidden;\n		-webkit-perspective: 1000;\n	}\n	caption,\n	.tp-caption {\n		opacity: 0;\n		position:absolute;\n		visibility: hidden;\n\n	}\n}\n\n\n/******************************\n	-	IE8 HACKS	-\n*******************************/\n.noFilterClass {\n	filter:none !important;\n}\n\n\n/******************************\n	-	SHADOWS		-\n******************************/\n.tp-bannershadow  {\n	position:absolute;\n\n	margin-left:auto;\n	margin-right:auto;\n	.user-select(none);\n\n}\n\n\n\n/********************************\n	-	FULLSCREEN VIDEO	-\n*********************************/\n.caption,\n.tp-caption{\n	&.fullscreenvideo {\n		left:0px;\n		top:0px;\n		position:absolute;\n		width:100%;\n		height:100%;\n		iframe	{ width:100% !important; height:100% !important;}\n	}\n}\n\n/************************\n	-	NAVIGATION	-\n*************************/\n\n\n.tpclear{clear:both;}\n\n.tp-bullets	{\n	z-index:1000;\n	position:absolute;\n	opacity: 1;\n	.transition(all 0.2s ease-out);\n	margin-bottom: 0px;\n	&.hidebullets	{\n		margin-bottom: -10px;\n		opacity: 0;\n	}\n	&.simplebullets.round .bullet{\n		cursor: pointer;\n		position: relative;\n		background: rgba(0,0,0,.6);\n		width: 8px;\n		height: 8px;\n		margin-right: 0px;\n		float: left;\n		margin-top: 0px;\n		margin-left: 5px;\n		.border-radius(8px);\n		border: 2px solid #fff;\n		opacity: .7;\n		.trans\n		&.last{\n			margin-right:3px;\n		}\n		&.selected,\n		&:hover{\n			opacity: 1;\n		}\n	}\n}\n\n/*************************************\n	-	TP ARROWS 	-\n**************************************/\n.tparrows{\n	opacity: 1;\n	.transition(opacity 0.2s ease-out);\n	&.hidearrows{\n		opacity: 0;\n	}\n}\n.tp-leftarrow,\n.tp-rightarrow{\n	cursor:pointer;\n	position:relative;\n	z-index:100;\n	width:40px;\n	height:40px;\n	&:hover{\n		background-position:bottom left;\n		opacity: .7;\n	}\n	&.default{\n\n	}\n}\n.tp-leftarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_left.png\") no-repeat top left;\n\n}\n.tp-rightarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_right.png\") no-repeat top left;\n}\n\n\n/****************************************************************************************************\n	-	TP THUMBS 	-\n*****************************************************************************************************\n\n - tp-thumbs & tp-mask Width is the width of the basic Thumb Container (500px basic settings)\n\n - .bullet width & height is the dimension of a simple Thumbnail (basic 100px x 50px)\n\n *****************************************************************************************************/\n\n\n.tp-bullets.tp-thumbs{\n	z-index:1000;\n	position:absolute;\n	padding:3px;\n	background-color:#fff;\n	width:500px;\n	height:50px; 			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n	margin-top:-50px;\n	.tp-mask{\n		width:500px;\n		height:50px;  			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n		overflow:hidden;\n		position:relative;\n		.tp-thumbcontainer	{	width:5000px; position:absolute;}\n	}\n	.bullet{\n		width:100px;\n		height:50px;\n		cursor:pointer;\n		overflow:hidden;\n		background:none;\n		margin:0;\n		float:left;\n		opacity: 0.5;\n		.transition(all 0.2s ease-out);\n		&:hover,\n		&.selected{\n			opacity: 1;\n		}\n	}\n	img{\n		max-width:100%;\n	}\n}\n\n\n\n\n\n/************************************\n		-	TP BANNER TIMER		-\n*************************************/\n.tp-bannertimer	{\n	width:100%;\n	height:10px;\n	background:url(\"@{plRoot}/sections/revslider/assets/timer.png\");\n	position:absolute;\n	z-index:200;\n	&.tp-bottom{\n		bottom:0px !important;\n		height:5px;\n	}\n}\n\n@media only screen and (min-width: 480px) and (max-width: 767px) {\n	.responsive .tp-bullets.tp-thumbs{\n		width:300px !important;\n		height:30px !important;\n		.tp-mask{\n			width:300px !important; height:30px !important;\n		}\n		.bullet{\n			width:60px !important;height:30px !important;\n		}\n	}\n\n}\n\n   @media only screen and (min-width: 0px) and (max-width: 479px) {\n			.responsive .tp-bullets	{	display:none}\n			.responsive .tparrows	{	display:none;}\n}\n\n/*********************************************\n	-	BASIC SETTINGS FOR THE BANNER	-\n***********************************************/\n\n.tp-simpleresponsive img {\n	.user-select(none);\n}\n\n\n\n.tp-simpleresponsive {\n	a{	text-decoration:none;}\n	ul {\n		list-style:none;\n		padding:0;\n		margin:0;\n	}\n	 > ul >li{\n		list-stye:none;\n		position:absolute;\n		visibility:hidden;\n	}\n\n}\n\n/*  CAPTION SLIDELINK   **/\n.caption.slidelink a div,\n.tp-caption.slidelink a div {\n	width:10000px;\n	height:10000px;\n	background:url(\"@{plRoot}/sections/revslider/assets/coloredbg.png\") repeat;\n}\n\n.tp-loader 	{\n	background:url(\"@{plRoot}/sections/revslider/assets/loader.gif\") no-repeat 10px 10px;\n	background-color:#fff;\n	margin:-22px -22px;\n	top:50%;\n	left:50%;\n	z-index:10000;\n	position:absolute;\n	width:44px;\n	height:44px;\n	.border-radius(3px);\n}\n\n\n.tp-transparentimg {	content:\"url(@{plRoot}/sections/revslider/assets/transparent.png)\"}\n.tp-3d				{	-webkit-transform-style: preserve-3d;\n						 -webkit-transform-origin: 50% 50%;\n					}\n\n.section-hero .pl-section-pad{\n	padding: 5%;\n}\n\n.pl-hero-wrap{\n	max-width:100%;\n	.box-sizing(border-box);\n	.pl-hero {\n\n		h1 {\n			font-size: 60px;\n			line-height: 1;\n			letter-spacing: -1px;\n			small{\n				display: block;\n			}\n		}\n		p {\n			margin: @baseLineHeight/4 0 @baseLineHeight;\n			font-size: 18px;\n			font-weight: 200;\n			line-height: 1.5em;\n		}\n	}\n	.pl-hero-image{\n		margin-bottom: 0;\n	}\n}\n\n\n\n.pl-hero.span6, .pl-hero-image.span6 {\n  margin-bottom:0;\n}\n\n.hero_image {\n  text-align:right;\n}\n\n.pl-hero-reverse {\n\n  .pl-hero-image {\n    margin-right: 21px;\n    margin-left: 0px;\n  }\n\n  .pl-hero {\n    float: right;\n  }\n}\n\n// Media Queries\n@media (max-width:768px) {\n\n  .hero_image,.pl-hero h1, .pl-hero p {\n    text-align:center;\n  }\n  .pl-hero.span6 .btn {\n    display:block;\n    margin-bottom:20px;\n  }\n\n}\n\n@media (max-width:480px) {\n  .pl-hero-wrap {\n    padding:20px;\n  }\n}\n.hentry {\n	.entry-title{\n		margin:0;\n	}\n	\n	\n	\n}\n\n.article-wrap{\n	&.meta-mode-author{\n		.wrp{\n			padding-left: 150px;\n			min-height: 250px;\n			@media (max-width: @resPhoneLandscape) {\n				padding-left: 0;\n			}\n		}\n	}\n	.author-tag{\n		position: absolute;\n		\n		float: left;\n		width: 120px;\n		text-align: right;\n		padding-right: 30px;\n		font-size: 12px;\n		line-height: 1.2em;\n		padding-bottom: 30px;\n		.author a{\n			font-size: 14px;\n			color:  @textColor;\n			font-weight: 600;\n			display: block;\n		}\n		img{\n			max-width: 65px;\n			.border-radius(100px);\n		}\n		p{\n			margin: 5px 0;\n			padding: 5px 0;\n		}\n		hr{\n			margin: 0 0 3px;\n			width: 45px;\n			text-align: right;\n			display: inline-block;\n			border: none; \n			border-bottom: 1px solid @baseContrast;\n		}\n		.tag-comments{\n			a, i{\n				color:  @textColor;\n				opacity: .4;\n				&:hover{\n					opacity: 1;\n				}\n			}\n		}\n		@media (max-width: @resPhoneLandscape) {\n			position: static;\n			display: block;\n			width: auto;\n			padding: 0;\n			max-width: none;\n			float: none;\n			p{\n				margin: 0 0 8px;\n			}\n			img{\n				float:left;\n			}\n			> hr{\n				display: none !important;\n			}\n\n		}\n	}\n}\n \n\n.entry_content{\n	img {\n		max-width: 100%;\n	}\n	> :first-child{\n		margin-top: 0;\n	}\n	> :last-child{\n		margin-bottom: 0;\n	}\n}\n\n.fpost .post-excerpt {\n	font-size: 1.03em;\n	line-height: 1.55em;\n}\n\n.tags {\n	font-size: 86%;\n	opacity: .7;\n	.note{\n		.noteText();\n	}\n}\n\n.pledit{\n	opacity: .7;\n	&:hover{\n		opacity: 1;\n	}\n}\n\n// Belongs under postloop\n\n// Hentry format/sizing needs to be moved to be conextual\n// Henry is going to represent all long form text styling\n\n.post-meta .c_img,\n.hentry .c_img{\n	display: block;\n	text-align: center;\n}\n\n.multi-post {\n	.hentry {\n		margin: 0 0 @baseLineHeight;\n		.hentry-pad{\n			padding-bottom: @baseLineHeight;\n		}\n	}\n	\n	\n	.clip_box{\n		margin: 0 0 @baseLineHeight;\n		border-color: @dark-base;\n		.clip{\n			width:47%;\n			clear: none;\n			float: left;\n			margin: 0;\n			.hentry-pad{\n				padding: 0 0 @baseLineHeight/2;\n			}\n			&.clip-right{\n				float: right;\n			}\n			.clip-header {\n				padding: 4px 0 4px 0;\n				display: table-cell;\n				width: 10000px;\n			}\n			.clip-thumb{\n				a{\n					float:left;\n					margin-right: 10px;\n				}\n				img{\n					padding: 2px;\n					display:block;\n					margin:0 0 0.4em;\n				}\n			}\n			\n			.post-excerpt{\n				font-size: 90%;\n				line-height: 1.6em;\n				margin-bottom:1.5em;\n			}\n\n			.entry-title,\n			.metabar{\n				padding: 0;\n			}\n		}\n	}\n\n}\n\n\n.post-heade