geLines Edited Rule ^^ //\n                \n// Active state \n// ------------         \n.dropdown-menu .active > a,\n.dropdown-menu .active > a:hover {\n  background-color: @linkColor;\n  #gradient > .vertical(@linkColor, darken(@linkColor, 5%));\n}  \n\n/////////////////////////////////////////////////////\n// Styling for the Download button on the landing page\n.btn-download {\n  color: #fff;\n  background-color: @linkColor;\n  background: @linkColor;\n  &:hover {\n      background-color: darken( @linkColor, 20% );\n      background: darken( @linkColor, 20% );\n      color: #fff;\n    }\n}\n\n/////////////////////////////////////////////////////\n// Styling for the Read More button in the hero slides\n.btn-read-more {\n    margin-top: 30px;\n    @media only screen and (max-width : 1024px) {\n      margin-top: 0px;\n    }\n    @media only screen and (max-width : 720px) {\n      margin-top: -30px;\n    }\n    &:hover {\n      background-color: darken( @btnBackground, 20% );\n      background: darken( @btnBackground, 20% );\n    }\n}\n\n/////////////////////////////////////////////////////\n// Add right-hand vertical separator bar to all but\n// the right-most widget on bottom of landing page. \n.landing-page-rhs-bar {\n  min-height: 200px;\n  border-right: thin solid @lightGray;\n  padding-right: 15px;\n  padding-left: 15px;\n}\n.contributor-row {\n  padding-bottom: 30px;\n}\n// Don\'t show that bar on mobile (because the items are stacked vertically)\n@media only screen and (max-width : @resPortraitTablet) {\n  .landing-page-rhs-bar {\n    border-right: none;\n  }\n  // Also add some space underneath to separate row elements when they are stacked vertically\n  .pl-section-pad .row:last-child > [class*=\"span\"] {\n    margin-bottom: 15px;\n  }\n  .contributor-row {\n    padding-bottom: 0px;\n  }\n}\n// Override default margins for span4, which is used in the landing page\n.columnizer > .span4 {\n  margin-left: auto;\n}\n\n\n////////////////////////////////////////