-region-bar,\n.pl-area-controls,\n.pl-column-forcer,\n.pl-sortable-buffer, \n.li-refresh, \n.pl-editor-only{\n	display: none;\n}\n\n.btn-region,\n.pl-section-controls{\n	font-family: @defaultFont;\n}\n\n.drag-drop-editing{\n\n	// Area Controls\n	// --------------------------------------------------\n\n	.pl-editor-only{\n		display: block;\n	}\n	.pl-region{\n		position: relative;	\n	}\n	.pl-region-bar{\n		display: block;\n	\n		z-index: @zindexEditor;\n		#gradient > .vertical(rgba(182, 242, 253, .1), rgba(125, 233, 253, .1));\n		border-top: 1px dashed @editorBorder;\n		width: 100%;\n		height: 11px;\n		line-height: 10px;\n		a.btn-region{\n			cursor: help;\n			#gradient > .vertical(rgba(182, 242, 253, .6), rgba(125, 233, 253, .6));\n			color: rgba(0,0,0,.6);\n			text-shadow: 0 1px 0 rgba(255,255,255,.4);\n			box-shadow: inset 2px 1px 0 rgba(255,255,255,.5);\n			border-right: 1px dashed @editorBorder;\n			font-size: 8px;\n			line-height: 13px;\n			padding: 0px 4px;\n			font-weight: bold;\n			vertical-align: middle;\n		}\n	}\n\n	.pl-area-container{\n		min-height: 20px;\n	}\n	.pl-area-placeholder{\n		max-height: 400px;\n		border-top: 1px dashed @editorBorder;\n		#gradient > .vertical(rgba(182, 242, 253, .06), rgba(125, 233, 253, .06));\n	}\n\n	.pl-area{\n		position: relative;\n		border-top: 1px dashed @editorBorder;\n		&.ui-sortable-helper{\n			border: 1px dashed @editorBorder;\n			.pl-area-controls{\n				display: none;\n			}\n		}\n		.pl-area-controls{\n			.transition(all 0.15s ease-in-out);\n			display: block;\n			z-index: @zindexEditor;\n			position: absolute;\n			left: 0;\n			top: 4px;\n			text-align: right;\n			width: 72px;\n			line-height: 1em;\n			font-size: 9px;\n\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			margin-left: -54px;\n\n			\n			&:hover{\n				opacity: 1;\n				margin-left: 0;\n				z-index: @zindexTooltip;\n			}\n			&::after{\n							content: \'\';\n							display: block;\n							width: 200px;\n							height: 160%