ctive{\n						background-color: #01c5df;\n						color:#222;\n					}\n				}\n\n			}\n\n		}\n		// Section Controls\n		.pl-section-controls{\n			position: absolute;\n			top: 0;\n			left: 0;\n			width: 100%;\n			z-index: @zindexEditor;\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			text-align: center;\n			\n			font-weight: bold;\n			font-size: 10px;\n			line-height: 21px;\n			.box-sizing(border-box);\n			&:hover{\n				cursor: move;\n			}\n			.controls-title,\n			.controls-right,\n			.controls-left{\n				display: inline;\n				clear: none;\n			}\n			.title-desc{\n				color: rgba(255,255,255, .5);\n			}\n			.controls-right{\n				padding-right: 4px;\n				float: right;\n			}\n			.controls-left{\n				padding-left: 4px;\n				float: left;\n			}\n			.s-control{\n				line-height: 16px;\n\n				font-size: 11px;\n				display: inline-block;\n				text-align: center;\n			}\n			span.s-control{\n				letter-spacing: .1em;\n			}\n			a.s-control{\n\n				width: 16px;\n				height: 16px;\n\n				padding: 0 1px;\n\n				.border-radius(3px);\n				background: rgba(0,0,0,.3);\n				&:hover{\n					background: rgba(0,0,0,.6);\n					color:#eee;\n				}\n				&:active{\n					background: rgba(0,0,0,.4);\n					color:#ccc;\n				}\n				.caret, &:hover .caret{\n				  border-top:   4px solid #fff;\n				  .opacity(60);\n				  content: \"\";\n				}\n				&.s-control-icon{\n					width: 16px;\n					height: 16px;\n					color:#fff;\n				}\n			}\n\n\n		}\n\n	}\n\n	.pl-sortable-area,\n	.pl-sortable-column{\n		min-height: 35px;\n		.transition(.5s);\n	}\n	&.pl-dragging{\n\n		.pl-sortable-column{\n			padding-top: 0px;\n		}\n		.section-plcolumn{\n			background: @pl-base;\n			outline: 1px dashed #35eafd;\n		}\n		.ui-sortable-helper{\n			border: 1px solid rgba(255,255,255,.4);\n			box-shadow: 0 1px 4px rgba(0,0,0,.5);\n			max-height: 350px;\n			overflow:hidden;\n			opacity: .5;\n		}\n		.pl-sortable-buffer{\n			height: 5px;\n			margin: 5px 0;\n			display: block;\n		}\n		&:hover,\n		&.ui-sortable-helper,\n		.section-plcolumn {\n			.pl-section-controls{\n				display: none !important;\n			}\n		}\n\n	}\n\n\n	// Area for dropping elements on drag\n	.pl-placeholder {\n		max-height: 200px;\n		display: inline;\n		float: left;\n		clear: none;\n		position: relative;\n		margin: 0 0 1em 0;\n		padding: 0;\n		.box-sizing(border-box);\n		.border-radius(5px);\n		#gradient > .vertical(rgba(182, 242, 253, .2), rgba(125, 233, 253, .2));\n		outline: 1px solid @editorBorderTrans;\n		// Used in nested columns\n		&.hidden-placeholder{\n			display: none;\n		}\n	}\n	.pl-sortable-area,\n	.pl-sortable-column{\n		.empty-area{\n			min-height: 35px;\n		}\n	}\n\n\n\n	.cant-offset .section-offset-increase,\n	.cant-offset .section-offset-reduce,\n	.cant-offset .offset-size,\n	.sortable-first .section-start-row{\n		.opacity(50);\n	}\n\n	.force-start-row .s-control.s-control-icon.section-start-row{\n		.opacity(100);\n		color: @editorBorder;\n		background: rgba(0,0,0,.7);\n	}\n\n\n\n	.loaded-notice{\n		.the-notice{\n			background:rgba(0,0,0,.6); \n			color:#fff; \n			display: inline-block; \n			padding: 5px 30px;\n			margin: 10px 0;\n			font-size: 11px;\n		}\n		text-align: center;\n		font-family: @buttonFontFamily;\n		font-weight: bold;\n	}\n\n\n}\n\n\n\n// Layout\n// --------------------------------------------------\n.width-resize{\n	// Resizing\n	.ui-resizable-handle {\n		&.ui-resizable-e,\n		&.ui-resizable-w{\n			cursor: move;\n			position: absolute;\n			font-size: 0.1px;\n			display: block;\n		}\n\n\n	}\n\n	&.resize-hover .ui-resizable-handle{\n		background: rgba(182, 242, 253, .2);\n\n	}\n	&.width-resizing .ui-resizable-handle{\n		background: rgba(182, 242, 253, .4);\n	}\n\n\n\n\n	.ui-resizable-e{\n		cursor: e-resize;\n		border-left: 1px dashed @editorBorder;\n		width: 5px;\n		right: -6px;\n		top: 0;\n		height: 100%;\n	}\n\n	.ui-resizable-w{\n		cursor: w-resize;\n		border-right: 1px dashed @editorBorder;\n		width: 5px;\n		left: -6px;\n		top: 0;\n		height: 100%;\n	}\n\n	&.width-resize-edge{\n		.ui-resizable-e{\n\n			border-left: 1px dashed @editorBorder;\n\n			right: 0;\n		}\n		.ui-resizable-w{\n\n			border-right: 1px dashed @editorBorder;\n\n			left: 0;\n\n		}\n	}\n\n	.footer .pl-area:last-child{\n		border-bottom: 1px dashed @editorBorder;\n	}\n	\n	.pl-content.nested-section-area{\n		border-top: 1px dashed @editorBorder;\n		border-bottom: 1px dashed @editorBorder;\n	}\n\n}\n\n\n// Need to refresh the page banner\n// --------------------------------------------------\n.pl-refresh-banner{\n	display: block;\n	padding: 20px;\n	text-align: center;\n	font-weight: bold;\n	opacity: .7;\n	.banner-content{\n		width: 130px;\n		margin: 0 auto;\n		.btn,\n		.banner-frame{\n			box-shadow: inset 1px 1px 0 rgba(255,255,255,.2), 1px 1px 0 rgba(255,255,255,.4);\n			border:1px solid rgba(0,0,0,.3);\n		}\n		.banner-frame{\n			max-width: 120px;\n			margin-bottom: 8px;\n			display: inline-block;\n			img{\n				max-width: 100%;\n			}\n		}\n	}\n	\n}\n\n\n.pl-credit,\n.toolbox-activate{\n	\n	position: fixed; \n	bottom: 0; \n	background: rgba(0,0,0,.9); \n	font-family: @buttonFontFamily;\n	font-weight: 300;\n	color: #fff; \n	text-shadow: 0 -1px 0 #000;\n	opacity: .6;\n	padding: 0 8px 0px 10px;\n	font-size: 15px;\n	height: 29px; \n	line-height: 29px;\n	text-align: center;\n	.transition(all .5s ease);\n	cursor: pointer;\n	.transit{\n		.transition(all .5s ease);\n	}\n	.fademein{\n		opacity: 0;\n		.transition(all .7s ease);\n	}\n	&:hover{\n		.fademein{\n			opacity: 1;\n		}\n		\n		color: #fff;\n		opacity: 1;\n		i{\n			color: #35eafd;\n			text-shadow: -1px 1px 8px rgba(53, 254, 253, .4), 1px -1px 8px rgba(53, 254, 253, .4);\n		}\n		\n	}\n}\n\n.pl-credit{\n	.border-top-left-radius(3px);\n	right: -33px; \n	box-shadow: -1px -1px 0 rgba(255,255,255,.2);\n	display: block !important;\n	visibility: visible !important;\n	opacity: 1 !important;\n	&:hover{\n		right:0;\n	}\n	&::after{\n		content: \'\';\n		display: block;\n		width: 200px;\n		height: 160%;\n		position: absolute;\n	