\n  background: transparent;\n  border: 0;\n  -webkit-appearance: none;\n}// --------------------------------------------------\n// PageLines Pager\n// --------------------------------------------------\n\n\n.pager {\n  list-style: none;\n  text-align: center;\n  .clearfix();\n	li {\n	  display: inline;\n	}\n	a,\n	span {\n		display: inline-block;\n		padding: 5px 14px;\n		.box-shadow(~\"1px 1px 0 @baseLighterMore, inset 1px 1px 0 @baseLighterMore\");\n		.buttonBackground(@baseContrastSlight, darken(@baseContrastSlight, 3));\n		border: 1px solid @baseContrastDark;\n		.border-radius(15px);\n	}\n	a:hover {\n	  text-decoration: none;\n	  background-color: @baseContrastMore;\n	}\n	a:active{\n		.box-shadow(~\"1px 1px 0 @baseLighterMore, inset 1px 1px 0 rgba(0,0,0,.2)\");\n		background-color: @baseContrastDark;\n		border: 1px solid @baseContrastDarkMore;\n	}\n	.next{\n		a, span{\n			float: right;\n		}\n	}\n	.previous a {\n	  	float: left;\n	}\n	.disabled {\n		a, a:hover, span {\n		  color: @baseContrast;\n		  background-color: @textColorSubtle;\n		  cursor: default;\n		}\n	}\n}\n//\n// Pagination (multiple pages)\n// --------------------------------------------------\n\n.pagination {\n	.wp-pagenavi{\n		// Reset\n		span.pages{\n			.noteText();\n			float: left;\n			line-height: (@baseLineHeight * 1.5) - 2;\n		}\n		a, span, a:hover, span.current {\n			text-decoration: none;\n			border: none;\n			padding: 0;\n			margin: 0;\n			font-weight: normal;\n		}\n		a, span.current{\n			.box-shadow(0 1px 2px rgba(0,0,0,.05));\n		}\n	}\n	ul{\n		.box-shadow(0 1px 2px rgba(0,0,0,.05));\n		.border-radius(3px);\n	}\n	ul, &.pagenavi {\n		display: inline-block;\n		.ie7-inline-block();\n		margin: 0;\n		> a span.pg,\n		> span.pg,\n		.wp-pagenavi a,\n		.wp-pagenavi span.current,\n		.wp-pagenavi span.extend{\n			float: left;\n			padding: 0 14px;\n			line-height: (@baseLineHeight * 1.5) - 2;\n			text-decoration: none;\n			background-color: @pl-base;\n			border: 1px solid @baseContrast;\n			border-left-width: 0;\n			color: @textColorSubtle;\n		}\n		> span.pg,\n		> a:hover span.pg,\n		.wp-pagenavi a:hover,\n		.wp-pagenavi span.current{\n			box-shadow: inset 1px 1px 0 @baseLighterMore;\n			background-color: @baseContrastSlight;\n			color: @pl-text;\n			text-decoration: none;\n		}\n		> li{\n		  display: inline;\n			> a,\n			> span{\n				float: left;\n				padding: 0 14px;\n				line-height: (@baseLineHeight * 1.5) - 2;\n				text-decoration: none;\n				background-color: @pl-base;\n				border: 1px solid @baseContrast;\n				border-left-width: 0;\n				color: @textColorAlt;\n			}\n			&.active > a,\n			&.active > span,\n			> a:hover{\n				background-color: @baseContrast;\n				text-decoration: none;\n			}\n		}\n		> .active a,\n		> .active span{\n			color: @textColorAlt;\n			cursor: default;\n		}\n		> li:first-child > a,\n		li:first-child > span,\n		> a:first-child > span,\n		> span:first-child,\n		.wp-pagenavi > span.pages + a,\n		.wp-pagenavi > span.pages + span.current{\n			border-left-width: 1px;\n			.border-radius(3px 0 0 3px);\n		}\n		> li:last-child > a,\n		li:last-child > span,\n		> a:last-child > span,\n		> span:last-child,\n		.wp-pagenavi a:last-child,\n		.wp-pagenavi span:last-child{\n			.border-radius(0 3px 3px 0);\n		}\n		> .disabled > span,\n		.disabled > a,\n		.disabled > a:hover {\n				opacity: .7;\n			  background-color: transparent;\n			  cursor: default;\n		}\n	}\n	.desc, .wp-pagenavi span.pages {\n		.noteText();\n		line-height: (@baseLineHeight * 1.5) - 2;\n\n		@media (max-width: 767px) { display: none;}\n	}\n}\n\n// Centered\n.pagination-centered {\n  text-align: center;\n}\n.pagination-right {\n  text-align: right;\n}\n//\n// Progress bars\n// --------------------------------------------------\n\n\n// ANIMATIONS\n// ----------\n\n// Webkit\n@-webkit-keyframes progress-bar-stripes {\n  from  { background-position: 40px 0; }\n  to    { background-position: 0 0; }\n}\n\n// Firefox\n@-moz-keyframes progress-bar-stripes {\n  from  { background-position: 40px