INSERT INTO `wp_options` VALUES (175341,'_transient_dash_4077549d03da2e451c8b5f002294ff51','<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2016/04/coleman/\'>WordPress 4.5 Coleman</a> <span class=\"rss-date\">April 12, 2016</span><div class=\"rssSummary\">Version 4.5 of WordPress, named Coleman in honor of jazz saxophonistColeman Hawkins, is available for download or update in your WordPress dashboard. New features in 4.5 help streamline your workflow, whether youre writing or building your site. Editing Improvements Inline Linking Stay focused on your writing with a less distracting interface that keeps you in [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'http://wptavern.com/sucuri-partners-with-lets-encrypt-to-offer-free-ssl-certificates-to-all-customers\'>WPTavern: Sucuri Partners with Lets Encrypt to Offer Free SSL Certificates to All Customers</a></li><li><a class=\'rsswidget\' href=\'https://poststatus.com/wordpress-4-5-draft-podcast/\'>Post Status: WordPress 4.5  Draft Podcast</a></li><li><a class=\'rsswidget\' href=\'http://wptavern.com/help-test-the-new-wordpress-importer-plugin-in-development\'>WPTavern: Help Test the New WordPress Importer Plugin in Development</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\'dashboard-news-plugin\'><span>Popular Plugin:</span> <a href=\'https://wordpress.org/plugins/siteorigin-panels/\' class=\'dashboard-news-plugin-link\'>Page Builder by SiteOrigin</a>&nbsp;<span>(<a href=\'plugin-install.php?tab=plugin-information&amp;plugin=siteorigin-panels&amp;_wpnonce=b60819d366&amp;TB_iframe=true&amp;width=600&amp;height=800\' class=\'thickbox\' title=\'Page Builder by SiteOrigin\'>Install</a>)</span></li></ul></div>','no');
