padding: 5px 0;\n		}\n		hr{\n			margin: 0 0 3px;\n			width: 45px;\n			text-align: right;\n			display: inline-block;\n			border: none; \n			border-bottom: 1px solid @baseContrast;\n		}\n		.tag-comments{\n			a, i{\n				color:  @textColor;\n				opacity: .4;\n				&:hover{\n					opacity: 1;\n				}\n			}\n		}\n		@media (max-width: @resPhoneLandscape) {\n			position: static;\n			display: block;\n			width: auto;\n			padding: 0;\n			max-width: none;\n			float: none;\n			p{\n				margin: 0 0 8px;\n			}\n			img{\n				float:left;\n			}\n			> hr{\n				display: none !important;\n			}\n\n		}\n	}\n}\n \n\n.entry_content{\n	img {\n		max-width: 100%;\n	}\n	> :first-child{\n		margin-top: 0;\n	}\n	> :last-child{\n		margin-bottom: 0;\n	}\n}\n\n.fpost .post-excerpt {\n	font-size: 1.03em;\n	line-height: 1.55em;\n}\n\n.tags {\n	font-size: 86%;\n	opacity: .7;\n	.note{\n		.noteText();\n	}\n}\n\n.pledit{\n	opacity: .7;\n	&:hover{\n		opacity: 1;\n	}\n}\n\n// Belongs under postloop\n\n// Hentry format/sizing needs to be moved to be conextual\n// Henry is going to represent all long form text styling\n\n.post-meta .c_img,\n.hentry .c_img{\n	display: block;\n	text-align: center;\n}\n\n.multi-post {\n	.hentry {\n		margin: 0 0 @baseLineHeight;\n		.hentry-pad{\n			padding-bottom: @baseLineHeight;\n		}\n	}\n	\n	\n	.clip_box{\n		margin: 0 0 @baseLineHeight;\n		border-color: @dark-base;\n		.clip{\n			width:47%;\n			clear: none;\n			float: left;\n			margin: 0;\n			.hentry-pad{\n				padding: 0 0 @baseLineHeight/2;\n			}\n			&.clip-right{\n				float: right;\n			}\n			.clip-header {\n				padding: 4px 0 4px 0;\n				display: table-cell;\n				width: 10000px;\n			}\n			.clip-thumb{\n				a{\n					float:left;\n					margin-right: 10px;\n				}\n				img{\n					padding: 2px;\n					display:block;\n					margin:0 0 0.4em;\n				}\n			}\n			\n			.post-excerpt{\n				font-size: 90%;\n				line-height: 1.6em;\n				margin-bottom:1.5em;\n			}\n\n			.entry-title,\n			.metabar{\n				padding: 0;\n			}\n		}\n	}\n\n}\n\n\n.post-head