e;\n	zoom: 1;\n	.slides {\n		zoom: 1;\n		> li {position: relative;}\n	}\n}\n\n/* Suggested container for \"Slide\" animation setups. Can replace this with your own, if you wish */\n.flex-container {zoom: 1; position: relative;}\n\n.flex-caption {\n	background:none;\n	zoom: 1;\n	text-align: left;\n	max-width: 42%;\n	padding: 13px 2%;\n	margin: 0;\n	position: absolute;\n	right: 0;\n	bottom: 0;\n	background: rgba(0,0,0,.4);\n	color: #fff;\n	text-shadow: 0 -1px 0 rgba(0,0,0,.6);\n	box-shadow: 1px 1px 0 rgba(255,255,255, .1), -1px -1px 0 rgba(255,255,255, .1);\n	font-size: 15px;\n	line-height: 1.5em;\n}\n\n/* Direction Nav */\n.flex-direction-nav {\n	height: 0;\n	li a{\n		text-indent:-99999px; //IE8\n		margin-top: -42px; /* 16 (half height of control nav) + 26 (half of height) */\n		width: 52px;\n		height: 52px;\n		display: block;\n		position: absolute;\n		top: 50%;\n		cursor: pointer;\n		color: transparent;\n		background-color: rgba(50,50,50,.5);\n		.border-radius(25px);\n		background-image:url(@{plSectionsRoot}/quickslider/images/bg_direction_nav.png);\n		&:hover{\n			background-color: rgba(50,50,50,.4);\n\n		}\n		&:active{\n			background-color: rgba(50,50,50,.8);\n		}\n		&.next {background-position: -52px 0; right: 15px;}\n		&.prev {left: 15px;}\n		&.disabled {\n			opacity: .3;\n			filter:alpha(opacity=30);\n			cursor: default;\n		}\n	}\n}\n.no-control-nav .flex-direction-nav li a{\n	margin-top: -26px;\n}\n\n.section-quickslider{\n	.content-pad{\n		padding-bottom: 0;\n\n	}\n	.wrap-no-control-nav{\n		padding-bottom: 10px;\n	}\n}\n\n/* Control Nav */\n.flex-control-nav {\n	width: 100%;\n	padding-top: 15px;\n	text-align: center;\n	li {\n		margin: 0 0 0 5px;\n		display: inline-block;\n		zoom: 1;\n		*display: inline;\n		&:first-child {margin: 0;}\n		a {\n			width: 13px;\n			height: 13px;\n			display: block;\n			cursor: pointer;\n			background:@baseContrastSlight;\n\n\n			.border-radius(6px);\n			cursor: pointer;\n			text-indent: -999em;\n			&.active {\n				background:@base