or: fadeout(@pl-text, 40%);\n	}\n	caption {\n		background-color: @dark-base;\n		font-size: .9em;\n		padding: 2px;\n		font-weight: normal;\n		font-weight: bold;\n	}\n	thead th {\n		background-color: lighten(@dark-base, 1.6);\n		padding: 2px;\n		font-size: .9em;\n	}\n	td{\n		padding:4px 8px;\n	}\n	#today{\n		background-color: @dark-base;\n		font-weight: bold;\n	}\n}\n\n// Align Search Left In Widgets\n.widget {\n	margin: 0;\n	margin-bottom: 1em;\n	overflow: hidden; //prevents responsive issues.\n	.widget-title, \n	.widgettitle{\n		> a{ color: inherit; }\n		text-align:left;\n		padding:3px 8px 3px 0px;\n		font-size: @h5Size;\n		margin-bottom: .5em;\n	}\n	.widget-pad{\n		padding: 10px;\n	}\n	&.widget_search .searchfield{\n		float:left;\n	}\n	.tagcloud{\n		padding: .7em;\n		line-height:1.4em;\n	}\n	&.widget_search .widget-title {\n		margin-bottom:10px;\n	}\n	&.widget_archive .postform,\n	&.widget_categories .postform {\n		margin:10px 10px;\n	}\n	&.widget_recent_comments li{\n		padding: 4px;\n	}\n	.textwidget {\n		padding: 5px 2px;\n		font-size: @fontSizeSmall;\n		> p, \n		> address, \n		> blockquote{\n			margin: 1em 0;\n			&:first-child{\n				margin-top: 0;\n			}\n			&:last-child{\n				margin-bottom: 0;\n			}\n		}\n	}\n	ul {\n		margin:0 0 10px .4em;\n		font-size: @fontSizeSmall;\n		&.sub-menu{\n			margin:0px;\n		}\n		li{\n\n			list-style:none;\n			display: block;\n			padding: 0 2px;\n			ul li{\n				margin-left: .3em;\n			}\n		}\n	}\n	.tags-list a{\n		display: inline-block; \n		border: 1px solid rgba(0,0,0,.1);\n		margin: 0 3px 3px 0;\n		padding: 1px 5px;\n		.border-radius(2px);\n	}\n}\n// Not needed?\n.pprow .widget {\n	margin:0;\n}\n\nul.sidebar_widgets{\n	list-style-type: none;\n}\n\n\n\n/* @ Comments */\n\n.wp-comments{\n	margin: 1em 0 0;\n	padding-top: 40px;\n    margin-top: -40px;\n	.wp-comments-pad{\n		padding: 10px 0;\n		> #respond{\n			input[type=\"text\"], textarea{\n				background: @baseContrastSlight;\n				border: 1px solid darken(@baseContrastSlight, 10);\n				color: @pl-text;\n			}\n		}\n	}\n}\n\n#respond{\n	margin: @baseLineHeight;\n	text-align: left;\n	\n	#reply-title {\n		font-size: 25px;\n\n	}\n	#commentform {\n		margin-top: 10px;\n		p{\n			margin: 0 0 10px;\n		}\n		small {\n			font-size: 90%;\n		}\n		label{\n			display: inline;\n		}\n		input[type=\"text\"] {\n			display: block;\n		}\n		textarea {\n			height: 140px;\n		}\n		#submit {\n			display: inline;\n		}\n		.required{\n			color: fadeout(@pl-text, 40%);\n		}\n\n		p.logged-in-as,\n		p.comment-form-comment,\n		p.form-submit{\n			margin: .3em 0;\n		}\n		p.comment-form-comment{\n			margin: .8em 0;\n		}\n	}\n}\n\np.form-allowed-tags{\n	width: auto;\n	margin-bottom: 1.6em;\n	font-size: .85em;\n	code{\n		background: transparent;\n		border: none;\n		display: block;\n		font-size: 10px;\n		line-height: 1.4em;\n		margin: 0 0 10px;\n		padding: 0;\n	}\n}\n\n.nocommentform{\n	display:none;\n}\n\n/* List of Comments */\n#comments-title {\n	font-size: 25px;\n}\n\n.wp-comments{\n	h3{\n		font-size: 25px;\n	}\n}\n\n.wp-comments .commentlist {\n	list-style-type:none;\n	margin: 10px 0px 10px 18px;\n	line-height: 1.6em;\n	> .comment {\n		padding-left: 80px;\n		border-bottom: 1px dashed @baseContrast;\n	}\n	.comment-body{\n		\n	}\n	p{\n		margin: 0 0 1em;\n	}\n	li {\n		padding: 20px;\n		ul li{\n			padding: 15px 0\n		}\n	}\n\n	img.avatar {\n		.border-radius(500px);\n		float: left;\n		margin-left: -75px;\n		position: relative;\n\n		.box-sizing(border-box);\n	}\n	cite {\n		font-weight: bold;\n		font-style: normal;\n	}\n	.comment-author{\n		font-size: 1.1em;\n	}\n	.comment-awaiting-moderation{\n		opacity: .7;\n		padding: 0 5px;\n		display: inline-block;\n		margin: 4px 0 8px 0;\n	}\n	.comment-meta{\n		margin: 0 0 @baseLineHeight;\n		display: block;\n		font-size: 11px;\n		line-height: 14px;\n		\n		a{\n			color: inherit;\n			opacity: .7;\n		}\n	}\n 	ul.children {\n		list-style-type: none;\n		.comment-body{\n			padding-left: 50px;\n		}\n		img.avatar {\n		width: 40px;\n		margin-top: 0;\n		margin-left: -50px;\n		color: inherit;\n		opacity: .7;\n		}\n		\n	}\n	\n	\n\n}\n\n// ----------------------------------------------------------------------\n// MULTISITE\n// ----------------------------------------------------------------------\n\n#multisite_area{\n	font-size: 1.1em;\n	line-height: 1.63em;\n	.mu_register {\n		width: auto;\n		\n		input[type=\"text\"]{\n			height: auto;\n			font-size: 35px;\n			background: @baseContrastSlight;\n			padding: 7px 6px;\n		}\n		input[type=\"submit\"]{\n			width: auto;\n			padding: 8px 18px;\n			font-size: @buttonFontSize;\n			font-family: @buttonFontFamily;\n		}\n		h1, \n		h2, \n		h3{\n			margin: 1em 0 .5em 0;\n		}\n		> h2{\n			font-size: 28px;\n		}\n		ul {\n			padding-left: 25px;\n			list-style-type: none;\n		}\n		p{\n			font-size: 16px;\n			line-height: 1.6em;\n			&.submit, \n			&.submit:hover, \n			&.submit:active{\n				background: none;\n				border: none;\n				box-shadow:none;\n				padding: 0;\n			}\n		}\n	}\n	.mu_alert{\n		margin: 1em 0;\n		font-weight: normal;\n	}\n}\n\n\n.gform_wrapper{\n	input[type=text], \n	input[type=url], \n	input[type=email], \n	input[type=tel], \n	input[type=number],  \n	input[type=password]{\n		font-size: 16px;\n		padding: 5px;\n	}\n}\n\n// ----------------------------------------------------------------------\n// Special/Misc WordPress Stuff\n// ----------------------------------------------------------------------\n\n#wpadminbar {\n	min-width: 800px;\n	.pl-admin-bar-label{\n		font-family: @buttonFontFamily;\n		font-size: .846em;\n		font-weight: bold;\n		line-height: 14px; // ensure proper line-height if floated\n		color: @white;\n		vertical-align: baseline;\n		white-space: nowrap;\n		text-shadow: 0 -1px 0 rgba(0,0,0,.35);\n		padding: 3px 4px 2px;\n		.border-radius(3px);\n	}\n}\n\n/* Search Reader Text */\n.screen-reader-text {\n	display:none;\n}\n#wpstats{\n	width:0px;\n	height:0px;\n	overflow:hidden;\n}\n\n// ----------------------------------------------------------------------\n// WooCommerce Tweaks\n// ----------------------------------------------------------------------\n.buttons_added{\n	input[type=\"button\"]{\n		.box-sizing( content-box );\n	}\n	input[type=\"number\"]{\n		margin-bottom: 0;\n	}\n}\n/* @ WP125 Advertising Widget */\n#page .widget_wp125{\n	padding: 0;\n	&.widget{\n		border:none;\n		padding:0;\n		margin:0 0 1em 0;\n		.wtitle{\n			display: none;\n		}\n		.winner {\n			padding:0;\n			line-height: 1px;\n		}\n	}\n	.winner  {\n		width:100%;\n	}\n	.wp125ad{\n		padding: 0;\n		width: auto;\n		float: left;\n		max-width: 50%;\n		a{\n			margin: 6px;\n			padding: 5px;\n			margin: 2px 5px 5px 5px;\n			display: inline-block;\n			border: 1px solid transparent;\n			background: @light-base;\n			border: 1px solid lighten(@light-base, 4);\n			box-shadow: 0 1px 2px rgba(0,0,0, .4);\n			img{\n				width: 125px;\n				height: auto;\n				display: block;\n				box-shadow: 0 1px 2px lighten(@light-base, 8);\n			}\n\n			&:hover{\n				background: lighten(@light-base, 4);\n			}\n		}\n	}\n\n}\n\n\n\n\n.widget ul.twitter .twitter-item{\n	font-weight: normal;\n	font-style: italic;\n	margin-bottom: .4em;\n	font-size: .9em;\n}\n\n\n\n\n\n\n/* @ Typekit */\n	.typekit-badge{ display: none !important; }\n\n/* FlickrRSS Widget */\n.widget_flickrRSS{\n	&.widget{\n		ul{\n			margin: 7px 0px 0px 0px;\n			li{display:inline-block;padding:0;margin:0;border-bottom:none;}\n		}\n	}\n	a{\n		display: inline-block;\n		margin: 8px 12px 2px 5px;\n		img{\n			display: block;\n			font-size: 10px;\n			border-width: 0px 1px 1px 0px;\n			border-style:solid;\n			padding: 3px;\n		}\n	}\n}\n\n.widget_flickrRSS a img,\n.srp-widget-thmblink{\n	background: @light-base;\n	border: 1px solid lighten(@light-base, 4);\n	box-shadow: 0 1px 2px rgba(0,0,0, .4);\n}\n\n.widget_flickrRSS a img:hover{\n	background: lighten(@light-base, 4);\n}\n\n/* @ Special Posts  */\n\n.widget {\n	div.srp-widget-singlepost{\n		border-bottom: 1px solid @dark-base;\n		font-size: .85em;\n		margin: 0;\n		padding: 1.2em 0;\n	}\n	.srp-widget-title-link{\n		font-size: .9em;\n		line-height: 1.2em;\n	}\n	.srp-widget-date{\n		color: fadeout(@pl-text, 40%);\n	}\n	div.srp-thumbnail-box{\n		padding-right: 15px;\n	}\n}\n\n.srp-widget-excerpt{\n	line-height: 1.5em;\n}\n.srp-widget-thmblink{\n	display: block;\n	padding: 3px;\n	text-decoration: none;\n	border: none;\n	text-shadow: none;\n	width: 50px;\n	img{display: block;}\n}\n\n/* @Disqus */\n#site #dsq-content{\n	text-shadow: none;\n	a{\n		text-shadow: none;\n	}\n	.dsq-request-user-info td a{\n\n		font-weight: normal;\n		font-size: 1.2em;\n		color: @pl-text;\n	}\n	.dsq-brlink {display: none;}\n	.dsq-request-user-info td small a {font-style:italic;}\n	h3 {\n		line-height:1.5em;\n		margin-bottom:1em;\n		border-color: @dark-base;\n	}\n}\n\n\n\n// CFORMS\n.cform {\n\n	input {\n		padding: 5px;\n		font-size: 1.1em;\n	}\n\n	fieldset{\n		border:none;\n	}\n\n	input[type=\"text\"].cf_error:focus {\n		border-color: rgba(82, 168, 236, 0.8);\n	}\n\n	label {\n		display:block;\n		margin:0;\n		text-align:left;\n	}\n\n	legend{display: none;}\n\n	.contentcontainer  .cform {\n		margin:5px 50px;\n		padding:0px 0;\n	}\n	ol, li {list-style-type: none; margin:0;padding:0;}\n	li {margin-bottom: .7em;}\n	.single {width: 97%;}\n	.area {width: 97%;}\n	.emailreqtxt,.reqtxt{font-style: italic;}\n	.cf_hidden {display: none;}\n\n	.success {\n		padding: 15px;\n		margin-bottom: .5em;\n		font-size:1.4em;\n		background-color: @dark-base;\n	}\n\n}\n/* Mailing List Form */\n.mailinglist .cform {\n	ol{width:262px; float: left;}\n	.single{width:97%}\n	.sendbutton{margin:21px 0 0 10px;padding: 3px 0 2px 0;}\n	.linklove a{display: none;}\n}\n\n\n/* Contact Forms 7 */\n\n#page {\n	div.wpcf7-mail-sent-ok{\n		border: 1px solid @successBorder;\n		background: @successBackground;\n	}\n	span.wpcf7-not-valid-tip{\n		border:1px solid @warningBorder;\n		background: @warningBackground;\n	}\n	div.wpcf7-validation-errors{\n		border: 1px solid @errorBorder;\n		background: @errorBackground;\n	}\n	div.wpcf7-mail-sent-ok,\n	span.wpcf7-not-valid-tip,\n	div.wpcf7-validation-errors{\n		text-shadow: 0 1px 0 rgba(255,255,255,.8);\n		text-align: center;\n		.box-shadow (inset 1px 1px 2px rgba(255,255,255,.5));\n	}\n}\n\n// Fluid\n.pagelines-grid{\n	padding: 1%;\n}\n\n// Draw Grid\n#grid > .fluid(@fluidGridColumnWidth, @fluidGridGutterWidth);// ALERT STYLES\n// ------------\n\n// Base alert styles\n.alert {\n	padding: 8px 35px 8px 14px;\n	margin-bottom: 10px;\n	text-shadow: 0 1px 0 rgba(255,255,255,.5);\n	background-color: @baseContrastSlight;\n	border-left: 5px solid @baseContrast;\n	\n	.close {\n		position: relative;\n		top: -2px;\n		right: -21px;\n		line-height: 18px;\n		font-size: 22px;\n		color: inherit;\n		opacity: .4;\n	}\n	p {\n		margin:0;\n	}\n	.btn-mini{\n		vertical-align: top;\n	}\n}\n.alert-heading {\n  color: inherit;\n}\n  \n	h2.alert-heading, \n	h3.alert-heading, \n	h4.alert-heading, \n	h5.alert-heading, \n	h6.alert-heading {\n		margin:0 auto 0.25em;\n	}\n\n\n\n// Alternate styles\n// ----------------\n\n.alert-warning {\n	background-color: @warningBackground;\n	border-color: @warningBorder;\n	color: @warningText;\n}\n.alert-success {\n  background-color: @successBackground;\n  border-color: @successBorder;\n  color: @successText;\n}\n.alert-important,\n.alert-error {\n  background-color: @errorBackground;\n  border-color: @errorBorder;\n  color: @errorText;\n}\n.alert-info {\n  background-color: @infoBackground;\n  border-color: @infoBorder;\n  color: @infoText;\n}\n\n// Block alerts\n// ------------------------\n.alert-block {\n  padding-top: 14px;\n  padding-bottom: 14px;\n}\n.alert-block > p,\n.alert-block > ul {\n  margin-bottom: 0;\n}\n.alert-block p + p {\n  margin-top: 5px;\n}\n// LABELS & BADGES\n// ---------------\n\n// Base classes\n.label,\n.badge {\n  font-family: @buttonFontFamily;\n  font-size: .846em;\n  font-weight: bold;\n  line-height: 14px; // ensure proper line-height if floated\n  color: @white;\n  vertical-align: baseline;\n  white-space: nowrap;\n  text-shadow: 0 -1px 0 rgba(0,0,0,.35);\n  background-color: @grayLight;\n}\n// Set unique padding and border-radii\n.label {\n  padding: 1px 4px 2px;\n  .border-radius(3px);\n}\n.badge {\n  padding: 1px 9px 2px;\n  .border-radius(9px);\n}\n\n// Hover state, but only for links\nbody a {\n  &.label:hover,\n  &.badge:hover {\n    color: @white;\n    text-decoration: none;\n    cursor: pointer;\n  }\n}\n\n// Colors\n// Only give background-color difference to links (and to simplify, we don\'t qualifty with `a` but [href] attribute)\n.label,\n.badge {\n  // Important (red)\n  &-important         { background-color: @btnImportantBackground;  }\n  &-important[href]:hover   { background-color: darken(@btnImportantBackground, 10%); }\n  // Warnings (orange)\n  &-warning           { background-color: @orange; }\n  &-warning[href]:hover     { background-color: darken(@orange, 10%); }\n  // Success (green)\n  &-success           { background-color: @successText; }\n  &-success[href]:hover     { background-color: darken(@successText, 10%); }\n  // Info (turquoise)\n  &-info              { background-color: @btnPrimaryBackground; }\n  &-info[href]:hover        { background-color: darken(@btnPrimaryBackground, 10%); }\n  // Inverse (black)\n  &-inverse           { background-color: @grayDark; }\n  &-inverse[href]:hover     { background-color: darken(@grayDark, 10%); }\n}\n\n// Quick fix for labels/badges in buttons\n.btn {\n  .label,\n  .badge {\n    position: relative;\n    top: -1px;\n  }\n}\n.btn-mini {\n  .label,\n  .badge {\n    top: 0;\n  }\n}// TOOLTIP\n// ------=\n\n.tooltip {\n  position: absolute;\n  z-index: @zindexTooltip;\n  display: block;\n  visibility: visible;\n  padding: 5px;\n  font-size: 11px;\n  .opacity(0);\n  &.in     { .opacity(80); }\n  &.top    { margin-top:  -2px; }\n  &.right  { margin-left:  2px; }\n  &.bottom { margin-top:   2px; }\n  &.left   { margin-left: -2px; }\n  &.top .tooltip-arrow    { #popoverArrow > .top(); }\n  &.left .tooltip-arrow   { #popoverArrow > .left(); }\n  &.bottom .tooltip-arrow { #popoverArrow > .bottom(); }\n  &.right .tooltip-arrow  { #popoverArrow > .right(); }\n}\n.tooltip-inner {\n	font-weight: normal;\n  max-width: 200px;\n  padding: 3px 8px;\n  color: @tooltipColor;\n  text-shadow: none;\n  text-align: center;\n  text-decoration: none;\n  background-color: @tooltipBackground;\n  .border-radius(4px);\nwhite-space: nowrap;\n}\n.tooltip-arrow {\n  position: absolute;\n  width: 0;\n  height: 0;\n}\n\n\n// POPOVERS\n// --------\n\n.popover {\n  position: absolute;\n  top: 0;\n  left: 0;\n  z-index: @zindexPopover;\n  display: none;\n  width: 236px;\n  padding: 1px;\n  background-color: @popoverBackground;\n  -webkit-background-clip: padding-box;\n     -moz-background-clip: padding;\n          background-clip: padding-box;\n  border: 1px solid #ccc;\n  border: 1px solid rgba(0,0,0,.2);\n  .border-radius(6px);\n  .box-shadow(0 5px 10px rgba(0,0,0,.2));\n\n  // Offset the popover to account for the popover arrow\n  &.top     { margin-top: -10px; }\n  &.right   { margin-left: 10px; }\n  &.bottom  { margin-top: 10px; }\n  &.left    { margin-left: -10px; }\n\n	.popover-title {\n	  margin: 0; // reset heading margin\n	  padding: 8px 14px;\n	  font-size: 14px;\n	  font-weight: normal;\n	  line-height: 18px;\n	  background-color: @popoverTitleBackground;\n	  border-bottom: 1px solid darken(@popoverTitleBackground, 5%);\n	  .border-radius(5px 5px 0 0);\n	}\n\n}\n\n\n.popover-content {\n  padding: 9px 14px;\n	p:first-child{\n		margin-top: 0;\n	}\n  p, ul, ol {\n    margin-bottom: 0;\n  }\n}\n\n// Arrows\n.popover .arrow,\n.popover .arrow:after {\n  position: absolute;\n  display: inline-block;\n  width: 0;\n  height: 0;\n  border-color: transparent;\n  border-style: solid;\n}\n.popover .arrow:after {\n  content: \"\";\n  z-index: -1;\n}\n\n.popover {\n  &.top .arrow {\n    bottom: -@popoverArrowWidth;\n    left: 50%;\n    margin-left: -@popoverArrowWidth;\n    border-width: @popoverArrowWidth @popoverArrowWidth 0;\n    border-top-color: @popoverArrowColor;\n    &:after {\n      border-width: @popoverArrowOuterWidth @popoverArrowOuterWidth 0;\n      border-top-color: @popoverArrowOuterColor;\n      bottom: -1px;\n      left: -@popoverArrowOuterWidth;\n    }\n  }\n  &.right .arrow {\n    top: 50%;\n    left: -@popoverArrowWidth;\n    margin-top: -@popoverArrowWidth;\n    border-width: @popoverArrowWidth @popoverArrowWidth @popoverArrowWidth 0;\n    border-right-color: @popoverArrowColor;\n    &:after {\n      border-width: @popoverArrowOuterWidth @popoverArrowOuterWidth @popoverArrowOuterWidth 0;\n      border-right-color: @popoverArrowOuterColor;\n      bottom: -@popoverArrowOuterWidth;\n      left: -1px;\n    }\n  }\n  &.bottom .arrow {\n    top: -@popoverArrowWidth;\n    left: 50%;\n    margin-left: -@popoverArrowWidth;\n    border-width: 0 @popoverArrowWidth @popoverArrowWidth;\n    border-bottom-color: @popoverArrowColor;\n    &:after {\n      border-width: 0 @popoverArrowOuterWidth @popoverArrowOuterWidth;\n      border-bottom-color: @popoverArrowOuterColor;\n      top: -1px;\n      left: -@popoverArrowOuterWidth;\n    }\n  }\n  &.left .arrow {\n    top: 50%;\n    right: -@popoverArrowWidth;\n    margin-top: -@popoverArrowWidth;\n    border-width: @popoverArrowWidth 0 @popoverArrowWidth @popoverArrowWidth;\n    border-left-color: @popoverArrowColor;\n    &:after {\n      border-width: @popoverArrowOuterWidth 0 @popoverArrowOuterWidth @popoverArrowOuterWidth;\n      border-left-color: @popoverArrowOuterColor;\n      bottom: -@popoverArrowOuterWidth;\n      right: -1px;\n    }\n  }\n}\n// BUTTON STYLES\n// -------------\n\n// Base styles\n// --------------------------------------------------\n\n// Core\n.btn {\n	display: inline-block;\n	.ie7-inline-block();\n	padding: 4px 10px 4px;\n	margin-bottom: 0; // For input.btn\n	font-size: @buttonFontSize;\n	line-height: @baseLineHeight - 1;\n	color: @grayDark;\n	text-align: center;\n	text-shadow: 0 1px 1px rgba(255,255,255,.75);\n	vertical-align: middle;\n	background:  @btnBackgroundHighlight;\n	.buttonBackground(@btnBackground, @btnBackgroundHighlight);\n	border: 1px solid @btnBorder;\n	border-bottom-color: darken(@btnBorder, 10%);\n	.border-radius(3px);\n	box-shadow: inset 0 1px 0 rgba(255,255,255,.2);\n	cursor: pointer;\n\n	// Give IE7 some love\n	.ie7-restore-left-whitespace();\n	\n	&:hover {\n		color: @grayDark;\n		text-decoration: none;\n		background-color: @btnBackgroundHighlight;\n		background-position: 0 -25px;\n\n		// transition is only when going to hover, otherwise the background\n		// behind the gradient (there for IE<=9 fallback) gets mismatched\n		.transition(background-position .1s linear);\n	}\n	&:focus {\n	  .tab-focus();\n	}\n	&.active,\n	&:active {\n		.translate3d(0, 1px, 0);\n		background-image: none;\n		box-shadow: inset 0 2px 2px rgba(0,0,0,.15), 0 1px 2px rgba(0,0,0,.05);\n	\n		background-color: darken(@white, 10%);\n		background-color: darken(@white, 15%) e(\"\\9\");\n		outline: 0;\n	}\n}\n\n// Hover state\nbody a.btn:hover {\n  text-decoration:none;\n}\n\n// Disabled state\n.btn.disabled,\n.btn[disabled] {\n  cursor: default;\n  background-image: none;\n  background-color: darken(@white, 10%);\n  .opacity(65);\n  .box-shadow(none);\n}\n\n\n// Button Sizes\n// --------------------------------------------------\n\n// Large\n.btn-large {\n	padding: 14px 20px;\n	font-size: @buttonFontSize + 4px;\n	line-height: normal;\n	.border-radius( 4px );\n	[class^=\"icon-\"] {\n		margin-top: 1px;\n	}\n}\n\n// Small\n.btn-small {\n	padding: @paddingSmall;\n	font-size: @fontSizeSmall;\n	.border-radius(@borderRadiusSmall);\n	[class^=\"icon-\"],\n	[class*=\" icon-\"] {\n	  margin-top: 0;\n	}\n}\n\n\n\n// Mini\n.btn-mini {\n	padding: 2px 6px;\n	font-size: 11px;\n	line-height: 14px;\n	[class^=\"icon-\"],\n	[class*=\" icon-\"] {\n 		margin-top: -1px;\n	}\n}\n\n\n// Alternate buttons\n// --------------------------------------------------\n\n// Set text color\n// -------------------------\n.btn-primary,\n.btn-primary:hover,\n.btn-warning,\n.btn-warning:hover,\n.btn-important,\n.btn-important:hover,\n.btn-success,\n.btn-success:hover,\n.btn-info,\n.btn-info:hover,\n.btn-inverse,\n.btn-inverse:hover, \n.btn-edit,\n.btn-edit:hover{\n  text-shadow: 0 -1px 0 rgba(0,0,0,.25);\n  color: @white;\n}\n// Provide *some* extra contrast for those who can get it\n.btn-primary.active,\n.btn-warning.active,\n.btn-important.active,\n.btn-success.active,\n.btn-info.active,\n.btn-inverse.active,\n.btn-edit.active {\n  color: rgba(255,255,255,.75);\n}\n\n// Set the backgrounds\n// -------------------------\n.btn-primary {\n  .buttonBackground(@btnPrimaryBackground, @btnPrimaryBackgroundHighlight);\n}\n// Warning appears are orange\n.btn-warning {\n	.buttonBackground(@btnWarningBackground, @btnWarningBackgroundHighlight);\n}\n// Important and error appear as red\n.btn-important {\n  .buttonBackground(@btnImportantBackground, @btnImportantBackgroundHighlight);\n}\n// Success appears as green\n.btn-success {\n  .buttonBackground(@btnSuccessBackground, @btnSuccessBackgroundHighlight);\n}\n// Info appears as a neutral blue\n.btn-info {\n  .buttonBackground(@btnInfoBackground, @btnInfoBackgroundHighlight);\n}\n// Inverse appears as dark gray\n.btn-inverse {\n  .buttonBackground(@btnInverseBackground, @btnInverseBackgroundHighlight);\n}\n.btn-edit {\n	.buttonBackground(rgba(70,70,70,.5), rgba(70,70,70,.7));\n	border: 1px solid rgba(0,0,0,.7);\n}\n\n.btn-link-icon{\n	> i:first-child {\n		color: @linkColor;\n	}\n}\n\n\n// Cross-browser Jank\n// --------------------------------------------------\n\nbutton.btn,\ninput[type=\"submit\"].btn {\n\n  // Firefox 3.6 only I believe\n  &::-moz-focus-inner {\n    padding: 0;\n    border: 0;\n  }\n\n  // IE7 has some default padding on button controls\n  *padding-top: 2px;\n  *padding-bottom: 2px;\n  &.btn-large {\n    *padding-top: 7px;\n    *padding-bottom: 7px;\n  }\n  &.btn-small {\n    *padding-top: 3px;\n    *padding-bottom: 3px;\n  }\n  &.btn-mini {\n    *padding-top: 1px;\n    *padding-bottom: 1px;\n  }\n}\n\n\n// BUTTON GROUPS\n// -------------\n\n\n// Make the div behave like a button\n.btn-group {\n    position: relative;\n    display: inline-block;\n    .ie7-inline-block();\n\n    vertical-align: middle; // match .btn alignment given font-size hack above\n    white-space: nowrap; // prevent buttons from wrapping when in tight spaces (e.g., the table on the tests page)\n    .ie7-restore-left-whitespace();\n\n}\n\n// Space out series of button groups\n.btn-group + .btn-group {\n  margin-left: 5px;\n}\n\n// Optional: Group multiple button groups together for a toolbar\n.btn-toolbar {\n  margin-top: @baseLineHeight / 2;\n  margin-bottom: @baseLineHeight / 2;\n  .btn-group {\n    display: inline-block;\n    .ie7-inline-block();\n  }\n}\n\n// Float them, remove border radius, then re-add to first and last elements\n.btn-group .btn {\n  position: relative;\n  float: left;\n  margin-left: -1px;\n  .border-radius(0);\n}\n// Set corners individual because sometimes a single button can be in a .btn-group and we need :first-child and :last-child to both match\n.btn-group .btn:first-child {\n  margin-left: 0;\n     -webkit-border-top-left-radius: 2px;\n         -moz-border-radius-topleft: 2px;\n             border-top-left-radius: 2px;\n  -webkit-border-bottom-left-radius: 2px;\n      -moz-border-radius-bottomleft: 2px;\n          border-bottom-left-radius: 2px;\n}\n.btn-group .btn:last-child,\n.btn-group .dropdown-toggle {\n     -webkit-border-top-right-radius: 2px;\n         -moz-border-radius-topright: 2px;\n             border-top-right-radius: 2px;\n  -webkit-border-bottom-right-radius: 2px;\n      -moz-border-radius-bottomright: 2px;\n          border-bottom-right-radius: 2px;\n}\n// Reset corners for large buttons\n.btn-group .btn.large:first-child {\n  margin-left: 0;\n     -webkit-border-top-left-radius: 2px;\n         -moz-border-radius-topleft: 2px;\n             border-top-left-radius: 2px;\n  -webkit-border-bottom-left-radius: 2px;\n      -moz-border-radius-bottomleft: 2px;\n          border-bottom-left-radius: 2px;\n}\n.btn-group .btn.large:last-child,\n.btn-group .large.dropdown-toggle {\n     -webkit-border-top-right-radius: 2px;\n         -moz-border-radius-topright: 2px;\n             border-top-right-radius: 2px;\n  -webkit-border-bottom-right-radius: 2px;\n      -moz-border-radius-bottomright: 2px;\n          border-bottom-right-radius: 2px;\n}\n\n// On hover/focus/active, bring the proper btn to front\n.btn-group .btn:hover,\n.btn-group .btn:focus,\n.btn-group .btn:active,\n.btn-group .btn.active {\n  z-index: 2;\n}\n\n// On active and open, don\'t show outline\n.btn-group .dropdown-toggle:active,\n.btn-group.open .dropdown-toggle {\n  outline: 0;\n}\n\n\n// Split button dropdowns\n\n// Give the line between buttons some depth\n.btn-group .dropdown-toggle {\n  padding-left: 8px;\n  padding-right: 8px;\n  \n  *padding-top: 3px;\n  *padding-bottom: 3px;\n}\n.btn-group .btn-mini.dropdown-toggle {\n  padding-left: 5px;\n  padding-right: 5px;\n  *padding-top: 1px;\n  *padding-bottom: 1px;\n}\n.btn-group .btn-small.dropdown-toggle {\n  *padding-top: 4px;\n  *padding-bottom: 4px;\n}\n.btn-group .btn-large.dropdown-toggle {\n  padding-left: 12px;\n  padding-right: 12px;\n}\n\n.btn-group.open {\n  // IE7\'s z-index only goes to the nearest positioned ancestor, which would\n  // make the menu appear below buttons that appeared later on the page\n  *z-index: @zindexDropdown;\n\n  // Reposition menu on open and round all corners\n  .dropdown-menu {\n    display: block;\n    margin-top: 1px;\n    .border-radius(5px);\n  }\n\n  .dropdown-toggle {\n    background-image: none;\n    @shadow: inset 0 1px 6px rgba(0,0,0,.15), 0 1px 2px rgba(0,0,0,.05);\n    .box-shadow(@shadow);\n  }\n}\n\n// Reposition the caret\n.btn .caret {\n  margin-top: 7px;\n  margin-left: 0;\n}\n.btn:hover .caret,\n.open.btn-group .caret {\n  .opacity(100);\n}\n// Carets in other button sizes\n.btn-mini .caret {\n  margin-top: 5px;\n}\n.btn-small .caret {\n  margin-top: 6px;\n}\n.btn-large .caret {\n  margin-top: 6px;\n  border-left:  5px solid transparent;\n  border-right: 5px solid transparent;\n  border-top:   5px solid @black;\n}\n\n\n// Account for other colors\n.btn-primary,\n.btn-warning,\n.btn-important,\n.btn-info,\n.btn-success,\n.btn-inverse {\n  .caret {\n    border-top-color: @white;\n    border-bottom-color: @white;\n    .opacity(75);\n  }\n}\n\n\n// Block button\n// -------------------------\n\n.btn-block {\n  display: block;\n  width: 100%;\n  padding-left: 0;\n  padding-right: 0;\n  .box-sizing(border-box);\n}\n\n// Vertically space out multiple block buttons\n.btn-block + .btn-block {\n  margin-top: 5px;\n}\n\n// Specificity overrides\ninput[type=\"submit\"],\ninput[type=\"reset\"],\ninput[type=\"button\"] {\n  &.btn-block {\n    width: 100%;\n  }\n}\n\n// Link buttons\n// --------------------------------------------------\n\n// Make a button look and behave like a link\n.btn-link,\n.btn-link:active,\n.btn-link[disabled] {\n  background-color: transparent;\n  background-image: none;\n  .box-shadow(none);\n}\n.btn-link {\n  border-color: transparent;\n  cursor: pointer;\n  color: @linkColor;\n  .border-radius(0);\n}\n.btn-link:hover,\n.btn-link:focus {\n  color: @linkColorHover;\n  text-decoration: underline;\n  background-color: transparent;\n}\n.btn-link[disabled]:hover,\n.btn-link[disabled]:focus {\n  color: @grayDark;\n  text-decoration: none;\n}\n\n// Typography.less\n// Headings, body text, lists, code, and more for a versatile and durable typography system\n// ----------------------------------------------------------------------------------------\n\n\n// BODY TEXT\n// ---------\nbody{\n	font-size: @baseFontSize;\n	font-family: @baseFontFamily;\n	font-weight: @baseFontWeight;\n	line-height: @propLineHeight;\n	color: @textColor;\n	\n}\n\n// PARAGRAPH\n// ---------\np {\n	margin: @propLineHeight 0;\n\n	small {\n		color: @textColorAlt;\n		font-size: @baseFontSize - 2;\n		line-height: @baseLineHeight / 2;\n	}\n\n	// SPECIAL FIXES\n\n	// fix for minimum paragraph widths\n	&:before{\n		content: \"\";\n		width: 8em;\n		display: block;\n		overflow: hidden;\n	}\n	hyphenate: auto;\n	hyphenate-before: 2;\n	hyphenate-after: 3;\n	hyphenate-lines: 3;\n	orphans: 4;\n}\n\n// Links\n// ---------\na{\n	color: @linkColor;\n	.transition(color .2s linear);\n	&:hover{\n		color: @linkColorHover;\n		text-decoration: none;\n	}\n}\n\n\n\n\n// SPECIAL MARKUP\n// --------------\ndt {\n	font-weight:bold;\n}\ndd {\n	font-style: italic;\n}\nsup {\n	font-size:.7em;\n	vertical-align: top;\n	vertical-align:super;\n}\nsub {\n	font-size:.7em;\n	vertical-align: bottom;\n}\n\nbig {\n	font-size: 1.3em;\n}\n\nabbr,\nacronym{\n	border-bottom: .1em dotted;\n	cursor: help;\n}\n\nq:before,\nq:after{\n	content: \"\'\";\n}\n\n// CODE / PRE\n// ----------\ncode,\npre {\n	padding: 0 3px 2px;\n	font-family: @codeFont;\n	font-size: 12px;\n	background-color: @dark-base;\n	border: 1px solid darken(@dark-base, 8);\n}\ncode {\n	padding: 2px 4px;\n}\npre {\n	display: block;\n	padding: 8.5px;\n	margin: 1em;\n	font-size: 12.025px;\n	line-height: 18px;\n	white-space: pre;\n	white-space: pre-wrap;\n	word-break: break-all;\n	word-wrap: break-word;\n	code {\n		padding: 0;\n		color: inherit;\n		background-color: transparent;\n		border: 0;\n	}\n	&.pre-scrollable {\n		max-height: 340px;\n		overflow-y: scroll;\n	}\n}\n\n// HEADINGS\n// --------\n\nh1, h2, h3, h4, h5, h6 {\n	margin: .4em 0;\n	text-rendering: optimizelegibility; // Fix the character spacing for headings\n	font-family: @headingsFontFamily;\n	font-weight: @headingsFontWeight;\n	line-height: 130%;\n	small {\n	  	font-weight: normal;\n		font-size: @h6Size;\n	}\n	> a{\n		color: inherit;\n		&:hover{\n			color: @linkColor;\n		}\n	}\n}\n.subhead{\n	color: @textColorAlt;\n	font-size: @smallerText;\n}\n\n\n// HEADINGS SIZING\n// ---------------\nh1,\n.fpost .entry-title{\n    font-size: @h1Size;\n    line-height: 110%;\n\n}\nh2 {\n  font-size: @h2Size;\n}\nh3 {\n  font-size: @h3Size;\n}\n\nh4 {\n  font-size: @h4Size;\n}\nh5 {\n  font-size: @h5Size;\n}\nh6 {\n  font-size: @h6Size;\n}\nh1, h2, h3, h4, h5, h6 {\n  line-height: 1.2em;\n  + p {margin-top:.4em;}\n	\n}\n\n\n/* @ Header Special Cases */\nh1 + img,\nh2 + img{\n	margin-top:1em;\n}\n\n// Page header\n.page-header {\n	padding-bottom: @baseLineHeight - 1;\n	margin: @baseLineHeight*3 0 @baseLineHeight;\n	border-bottom: 1px solid @hrBorder;\n	h1{\n		line-height: 1;\n		margin: 0;\n	}\n}\n.page-header-minor {\n	margin: @baseLineHeight 0;\n	padding: @baseLineHeight/2 0 ;\n	border-bottom: 1px solid @hrBorder;\n}\n\n\n\n\n// LISTS\n// -----\n\n\n\n// HENTRY = Standard Long Form Text Formatting\n// -------------------------------------------------\n.hentry{	\n	ul, \n	ol{\n		padding: 0;\n		margin: 1.3em 0 1.3em 1.3em;\n		font-size: 90%;\n		line-height: 1.3em;\n	}\n	ol{\n		list-style:decimal\n		ol{\n			list-style:lower-roman\n			ol{\n				list-style:lower-alpha\n			}\n		}\n	}\n	ul {\n	  list-style: disc;\n	}\n	ol {\n	  list-style: decimal;\n	}\n\n	ul ul,\n	ul ol,\n	ol ol,\n	ol ul {\n	  margin-bottom: 0;\n	}\n	\n	h1, h2, h3, h4, h5, h6{\n		margin: 1em 0;\n	}\n\n}\n\n// Unordered and Ordered lists\n\nol,\nul{\n	list-style-type:none\n}\n\nul.unstyled,\nol.unstyled {\n  margin-left: 0;\n  list-style: none;\n}\n\n// Description Lists\ndl {\n  margin-bottom: @baseLineHeight;\n}\ndt,\ndd {\n  line-height: @baseLineHeight;\n}\ndt {\n  font-weight: bold;\n  line-height: @baseLineHeight - 1; // fix jank Helvetica Neue font bug\n}\ndd {\n  margin-left: @baseLineHeight / 2;\n}\n\n// Horizontal layout (like forms)\n.dl-horizontal {\n  dt {\n    float: left;\n    clear: left;\n    width: 120px;\n    text-align: right;\n  }\n  dd {\n    margin-left: 130px;\n  }\n}\n\n// Links\na {\n	text-decoration:none;\n	&:focus{\n		outline:1px dotted invert;\n	}\n	&:active, &:focus{\n		outline: none !important;\n	}\n}\n\n\ninput.searchfield:active {\n   	outline: none !important;\n}\n\n\n// Horizontal rules\nhr {\n	clear:both;\n	height:0;\n	margin: @baseLineHeight 0;\n	border: 0;\n	border-top: 1px solid @hrBorder;\n	border-bottom: 1px solid @white;\n	&.soften {\n		height: 1px;\n		background-image: -webkit-linear-gradient(left, transparent, rgba(0, 0, 0, .1), transparent);\n		background-image: -moz-linear-gradient(left, transparent, rgba(0, 0, 0, .1), transparent);\n		background-image: -ms-linear-gradient(left, transparent, rgba(0, 0, 0, .1), transparent);\n		background-image: -o-linear-gradient(left, transparent, rgba(0, 0, 0, .1), transparent);\n		border: 0;\n	}\n}\n\n\n// Emphasis\nstrong {\n  font-weight: @baseFontWeight + 200;\n}\nem {\n  font-style: italic;\n}\n.muted {\n  color: @grayLight;\n}\n\n// Special Ampersand\n.spamp{\n	font-family: \'Baskerville\', Palatino, georgia;\n	font-style: italic;\n	font-weight: normal;\n}\n\n// Serif Style\n.serifize{\n	font-family: georgia, times, serif;\n	font-style: italic;\n	font-weight: normal;\n}\n\n// Abbreviations and acronyms\nabbr[title] {\n  border-bottom: 1px dotted #ddd;\n  cursor: help;\n}\nabbr.initialism {\n  font-size: 90%;\n  text-transform: uppercase;\n}\n\n// Blockquotes\nblockquote {\n	padding: 0 0 0 15px;\n	margin: 0 0 @baseLineHeight;\n	border-left: 5px solid @textColorSubtle;\n	p {\n		margin-bottom: 0;\n		font-style: italic;\n		&:first-child{\n			margin-top: 0;\n		}\n		&:last-child{\n			margin-bottom: 0;\n		}\n	}\n	small, \n	cite{\n		display: block;\n		font-size: 80%;\n		line-height: 1.5em;\n		padding-left: 10px;\n		color: @textColorAlt;\n		&:before {\n			content: \'\\2014 \\00A0\';\n		}\n	}\n	\n	&.side-right{\n		float: right;\n		max-width: 250px;\n		margin: 0 0 1em 1em;\n	}\n	&.side-left{\n		float: left;\n		max-width: 250px;\n		margin: 0 1em 1em -1em;\n	}\n	\n  // Float right with text-align: right\n  &.pull-right {\n    float: right;\n    padding-left: 0;\n    padding-right: 15px;\n    border-left: 0;\n    border-right: 5px solid @textColorSubtle;\n    p,\n    small {\n      text-align: right;\n    }\n  }\n}\n\n// Quotes\nq:before,\nq:after,\nblockquote:before,\nblockquote:after {\n  content: \"\";\n}\n\n// Addresses\naddress {\n  display: block;\n  margin-bottom: @baseLineHeight;\n  line-height: @baseLineHeight;\n  font-style: normal;\n}\n\n// Misc\nsmall {\n  font-size: 100%;\n}\ncite {\n  font-style: normal;\n}\n\n::selection {\n	background: @linkColor; /* Safari */\n	}\n::-moz-selection {\n	background: @linkColor; /* Firefox */\n}\n\n// -----------\n// Image Styles\n// styled images added from Bootstrap\n// PageLines 2.3\n// BA\n// -----------\n\n.muted {\n  color: @grayLight;\n}\n.text-warning {\n  color: @warningText;\n}\n.text-error {\n  color: @errorText;\n}\n.text-info {\n  color: @infoText;\n}\n.text-success {\n  color: @successText;\n}\n//\n// Dropdown menus\n// --------------------------------------------------\n\n\n// Use the .menu class on any <li> element within the topbar or ul.tabs and you\'ll get some superfancy dropdowns\n.dropup,\n.dropdown {\n  position: relative;\n}\n.dropdown-toggle {\n  // The caret makes the toggle a bit too tall in IE7\n  *margin-bottom: -3px;\n}\n.dropdown-toggle:active,\n.open .dropdown-toggle {\n  outline: 0;\n}\n\n// Dropdown arrow/caret\n// --------------------\n.caret {\n  display: inline-block;\n  width: 0;\n  height: 0;\n  vertical-align: top;\n  border-top:   4px solid rgba(0,0,0, .3);\n  border-right: 4px solid transparent;\n  border-left:  4px solid transparent;\n  content: \"\";\n}\n\n// Place the caret\n.dropdown .caret {\n  margin-top: 8px;\n  margin-left: 2px;\n}\n\n// The dropdown menu (ul)\n// ----------------------\nbody .dropdown-menu {\n	position: absolute;\n	top: 100%;\n	left: 0;\n	z-index: @zindexDropdown;\n	display: none; // none by default, but block on \"open\" of the menu\n	float: left;\n	min-width: 160px;\n	padding: 5px 0;\n	margin: 2px 0 0; // override default ul\n	list-style: none;\n	background-color: @dropdownBackground;\n	border: 1px solid #ccc; // Fallback for IE7-8\n	border: 1px solid @dropdownBorder;\n	*border-right-width: 2px;\n	*border-bottom-width: 2px;\n	.border-radius(6px);\n	.box-shadow(0 5px 10px rgba(0,0,0,.2));\n	.background-clip(padding-box);\n\n\n  // Aligns the dropdown menu to right\n  &.pull-right {\n    right: 0;\n    left: auto;\n  }\n\n  // Dividers (basically an hr) within the dropdown\n  .divider {\n    .nav-divider(@dropdownDividerTop, @dropdownDividerBottom);\n  }\n\n  // Links within the dropdown menu\n	li > a,\n	li > span {\n		display: block;\n		padding: 3px 20px;\n		clear: both;\n		font-weight: normal;\n		line-height: @baseLineHeight;\n		color: @dropdownLinkColor;\n		white-space: nowrap;\n		cursor: pointer;\n		.transition(none);\n	}\n}\n\n// Hover state\n// -----------\n.dropdown-menu li > a:hover,\n.dropdown-menu li > a:focus,\n.dropdown-submenu:hover > a,\n.dropdown-menu li.current-menu-item > a {\n  text-decoration: none;\n	text-shadow: 0 -1px 0 rgba(0,0,0,.5);\n  color: @dropdownLinkColorHover;\n  background-color: @dropdownLinkBackgroundHover;\n  #gradient > .vertical(@dropdownLinkBackgroundHover, darken(@dropdownLinkBackgroundHover, 5%));\n}\n// ^^ PageLines Edited Rule ^^ //\n\n// Active state\n// ------------\n.dropdown-menu .active > a,\n.dropdown-menu .active > a:hover {\n  color: @dropdownLinkColorHover;\n  text-decoration: none;\n  outline: 0;\n  background-color: @dropdownLinkBackgroundActive;\n  #gradient > .vertical(@dropdownLinkBackgroundActive, darken(@dropdownLinkBackgroundActive, 5%));\n}\n\n// Disabled state\n// --------------\n// Gray out text and ensure the hover state remains gray\n.dropdown-menu .disabled > a,\n.dropdown-menu .disabled > a:hover {\n  color: @grayLight;\n}\n// Nuke hover effects\n.dropdown-menu .disabled > a:hover {\n	text-decoration: none;\n	text-shadow: none;\n	background-color: transparent;\n	background-image: none; // Remove CSS gradient\n	.reset-filter();\n	cursor: default;\n}\n\n// Open state for the dropdown\n// ---------------------------\n.open {\n  // IE7\'s z-index only goes to the nearest positioned ancestor, which would\n  // make the menu appear below buttons that appeared later on the page\n  *z-index: @zindexDropdown;\n\n  & > .dropdown-menu {\n    display: block;\n  }\n}\n\n// Right aligned dropdowns\n// ---------------------------\n.pull-right > .dropdown-menu {\n  right: 0;\n  left: auto;\n}\n\n// Allow for dropdowns to go bottom up (aka, dropup-menu)\n// ------------------------------------------------------\n// Just add .dropup after the standard .dropdown class and you\'re set, bro.\n// TODO: abstract this so that the navbar fixed styles are not placed here?\n.dropup,\n.navbar-fixed-bottom .dropdown {\n  // Reverse the caret\n  .caret {\n    border-top: 0;\n    border-bottom: 4px solid @black;\n    content: \"\";\n  }\n  // Different positioning for bottom up menu\n  .dropdown-menu {\n    top: auto;\n    bottom: 100%;\n    margin-bottom: 1px;\n  }\n}\n\n// Sub menus\n// ---------------------------\n.dropdown-submenu {\n  position: relative;\n}\n.dropdown-submenu > .dropdown-menu {\n	top: 0;\n	left: 100%;\n	margin-top: -6px;\n	margin-left: -1px;\n	.border-radius(0 6px 6px 6px);\n}\n.dropdown-submenu:hover > .dropdown-menu {\n  display: block;\n}\n\n.dropdown-submenu > a:after {\n  display: block;\n  content: \" \";\n  float: right;\n  width: 0;\n  height: 0;\n  border-color: transparent;\n  border-style: solid;\n  border-width: 5px 0 5px 5px;\n  border-left-color: darken(@dropdownBackground, 20%);\n  margin-top: 5px;\n  margin-right: -10px;\n}\n.dropdown-submenu:hover > a:after {\n  border-left-color: @dropdownLinkColorHover;\n}\n\n\n// Tweak nav headers\n// -----------------\n// Increase padding from 15px to 20px on sides\n.dropdown .dropdown-menu .nav-header {\n  padding-left: 20px;\n  padding-right: 20px;\n}\n\n// Typeahead\n// ---------\n.typeahead {\n  margin-top: 2px; // give it some space to breathe\n  .border-radius(4px);\n}\n// ACCORDION\n// ---------\n\n// Parent container\n.accordion {\n  margin-bottom: @baseLineHeight;\n}\n\n// Group == heading + body\n.accordion-group {\n  background:darken(@pl-base,2);\n  margin-bottom: 2px;\n  border: 1px solid darken(@pl-base, 6);\n  .border-radius(4px);\n}\n.accordion-heading {\n  background:darken(@pl-base,5);\n  border-bottom: 0;\n\n   &:hover {\n    .border-radius(4px);\n	   background: @dark-base;\n	}\n   a:hover {\n		text-decoration:none;\n	}\n\n}\n.accordion-heading .accordion-toggle {\n  display: block;\n  padding: 8px 15px;\n}\n\n// Inner needs the styles because you can\'t animate properly with any styles on the element\n.accordion-inner {\n  padding: 9px 15px;\n  border-top: 1px solid darken(@pl-base, 6);\n}\n// CAROUSEL\n// --------\n\n.carousel {\n  position: relative;\n  margin-bottom: @baseLineHeight;\n  line-height: 1;\n}\n\n.carousel-inner {\n  overflow: hidden;\n  width: 100%;\n  position: relative;\n}\n\n.carousel {\n\n  h4 {\n    margin-top:0;\n  }\n\n  .item {\n    display: none;\n    position: relative;\n    .transition(.6s ease-in-out left);\n  }\n\n  // Account for jankitude on images\n  .item > img {\n    display: block;\n    line-height: 1;\n    width:100%;\n  }\n\n  .active,\n  .next,\n  .prev { display: block; }\n\n  .active {\n    left: 0;\n  }\n\n  .next,\n  .prev {\n    position: absolute;\n    top: 0;\n    width: 100%;\n  }\n\n  .next {\n    left: 100%;\n  }\n  .prev {\n    left: -100%;\n  }\n  .next.left,\n  .prev.right {\n    left: 0;\n  }\n\n  .active.left {\n    left: -100%;\n  }\n  .active.right {\n    left: 100%;\n  }\n\n}\n\n// Left/right controls for nav\n// ---------------------------\n\n.carousel-control, .hentry .carousel-control {\n  position: absolute;\n  top: 40%;\n  left: 15px;\n  width: 40px;\n  height: 40px;\n  margin-top: -20px;\n  font-family: @baseFontFamily;\n  font-size: 60px;\n  font-weight: 100;\n  line-height: 30px;\n  color: @white;\n  text-align: center;\n  background: @grayDarker;\n  border: 3px solid @white;\n  .border-radius(23px);\n  .opacity(50);\n\n  // we can\'t have this transition here\n  // because webkit cancels the carousel\n  // animation if you trip this while\n  // in the middle of another animation\n  // ;_;\n  // .transition(opacity .2s linear);\n\n  // Reposition the right one\n  &.right {\n    left: auto;\n    right: 15px;\n  }\n\n  // Hover state\n  &:hover {\n    color: @white;\n    text-decoration: none;\n    .opacity(90);\n  }\n}\n\n// Caption for text below images\n// -----------------------------\n\n.carousel-caption {\n  position: absolute;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  padding: 10px 15px 5px;\n  background: @grayDark;\n  background: rgba(0,0,0,.75);\n}\n  .carousel-caption p {\n    margin:0 0 0.8em 0;\n    color: @white;\n  }\n  .carousel-caption h4{\n    color: @white;\n  }\n// NAVIGATIONS\n// -----------\n\n\n\n// BASE CLASS\n// ----------\n\n.nav {\n  margin-left: 0;\n  margin-bottom: @baseLineHeight;\n  list-style: none;\n}\n\n// Make links block level\n.nav > li > a {\n  display: block;\n}\n.nav > li > a:hover {\n  text-decoration: none;\n  background-color: @grayLighter;\n}\n\n// Nav headers (for dropdowns and lists)\n.nav .nav-header {\n  display: block;\n  padding: 3px 15px;\n  font-size: 11px;\n  font-weight: bold;\n  line-height: @baseLineHeight;\n  color: @grayLight;\n  text-shadow: 0 1px 0 rgba(255,255,255,.5);\n  text-transform: uppercase;\n}\n// Space them out when they follow another list item (link)\n.nav li + .nav-header {\n  margin-top: 9px;\n}\n\n\n// NAV LIST\n// --------\n\n.nav-list {\n  padding-left: 15px;\n  padding-right: 15px;\n  margin-bottom: 0;\n}\n.nav-list > li > a,\n.nav-list .nav-header {\n  margin-left:  -15px;\n  margin-right: -15px;\n  text-shadow: 0 1px 0 rgba(255,255,255,.5);\n}\n.nav-list > li > a {\n  padding: 3px 15px;\n}\n.nav-list > .active > a,\n.nav-list > .active > a:hover {\n  color: @white;\n  text-shadow: 0 -1px 0 rgba(0,0,0,.2);\n  background-color: @linkColor;\n}\n.nav-list [class^=\"icon-\"] {\n  margin-right: 2px;\n}\n// Dividers (basically an hr) within the dropdown\n.nav-list .divider {\n  .nav-divider();\n}\n\n\n\n// TABS AND PILLS\n// -------------\n\n// Common styles\n.nav-tabs,\n.nav-pills {\n  .clearfix();\n}\n.nav-tabs > li,\n.nav-pills > li {\n  float: left;\n}\n.nav-tabs > li > a,\n.nav-pills > li > a {\n  padding-right: 12px;\n  padding-left: 12px;\n  margin-right: 2px;\n  line-height: 14px; // keeps the overall height an even number\n}\n\n// TABS\n// ----\n\n// Give the tabs something to sit on\n.nav-tabs {\n  border-bottom: 1px solid darken(@pl-base, 6);\n}\n\n// Make the list-items overlay the bottom border\n.nav-tabs > li {\n  margin-bottom: -1px;\n}\n\n// Actual tabs (as links)\n.nav-tabs > li > a {\n  padding-top: 8px;\n  padding-bottom: 8px;\n  line-height: @baseLineHeight;\n  border: 1px solid transparent;\n  .border-radius(4px 4px 0 0);\n  &:hover {\n    background-color:darken(@pl-base,2);\n    border-color: darken(@pl-base, 6);\n  }\n}\n// Active state, and it\'s :hover to override normal :hover\n.nav-tabs > .active > a,\n.nav-tabs > .active > a:hover {\n  color: @pl-text;\n  background-color: darken(@pl-base,2);\n  border: 1px solid darken(@pl-base, 6);\n  border-bottom-color: transparent;\n  cursor: default;\n}\n\n// PILLS\n// -----\n\n// Links rendered as pills\n.nav-pills > li > a {\n  padding-top: 8px;\n  padding-bottom: 8px;\n  margin-top: 2px;\n  margin-bottom: 2px;\n  .border-radius(5px);\n}\n\n// Active state\n.nav-pills > .active > a,\n.nav-pills > .active > a:hover {\n  color: @white;\n  background-color: @linkColor;\n}\n\n\n\n// STACKED NAV\n// -----------\n\n// Stacked tabs and pills\n.nav-stacked > li {\n  float: none;\n}\n.nav-stacked > li > a {\n  margin-right: 0; // no need for the gap between nav items\n}\n\n// Tabs\n.nav-tabs.nav-stacked {\n  border-bottom: 0;\n}\n.nav-tabs.nav-stacked > li > a {\n  border: 1px solid #ddd;\n  .border-radius(0);\n}\n.nav-tabs.nav-stacked > li:first-child > a {\n  .border-radius(4px 4px 0 0);\n}\n.nav-tabs.nav-stacked > li:last-child > a {\n  .border-radius(0 0 4px 4px);\n}\n.nav-tabs.nav-stacked > li > a:hover {\n  border-color: #ddd;\n  z-index: 2;\n}\n\n// Pills\n.nav-pills.nav-stacked > li > a {\n  margin-bottom: 3px;\n}\n.nav-pills.nav-stacked > li:last-child > a {\n  margin-bottom: 1px; // decrease margin to match sizing of stacked tabs\n}\n\n\n\n// DROPDOWNS\n// ---------\n\n// Position the menu\n.nav-tabs .dropdown-menu,\n.nav-pills .dropdown-menu {\n  margin-top: 1px;\n  border-width: 1px;\n}\n.nav-pills .dropdown-menu {\n  .border-radius(4px);\n}\n\n// Default dropdown links\n// -------------------------\n// Make carets use linkColor to start\n.nav-tabs .dropdown-toggle .caret,\n.nav-pills .dropdown-toggle .caret {\n  border-top-color: @linkColor;\n  border-bottom-color: @linkColor;\n  margin-top: 6px;\n}\n.nav-tabs .dropdown-toggle:hover .caret,\n.nav-pills .dropdown-toggle:hover .caret {\n  border-top-color: @linkColorHover;\n  border-bottom-color: @linkColorHover;\n}\n\n// Active dropdown links\n// -------------------------\n.nav-tabs .active .dropdown-toggle .caret,\n.nav-pills .active .dropdown-toggle .caret {\n  border-top-color: @grayDark;\n  border-bottom-color: @grayDark;\n}\n\n// Active:hover dropdown links\n// -------------------------\n.nav > .dropdown.active > a:hover {\n  color: @black;\n  cursor: pointer;\n}\n\n// Open dropdowns\n// -------------------------\n.nav-tabs .open .dropdown-toggle,\n.nav-pills .open .dropdown-toggle,\n.nav > .open.active > a:hover {\n  color: @white;\n  background-color: @grayLight;\n  border-color: @grayLight;\n}\n.nav .open .caret,\n.nav .open.active .caret,\n.nav .open a:hover .caret {\n  border-top-color: @white;\n  border-bottom-color: @white;\n  .opacity(100);\n}\n\n// Dropdowns in stacked tabs\n.tabs-stacked .open > a:hover {\n  border-color: @grayLight;\n}\n\n// PageLines Specific\n.hentry .nav-tabs > li{\n  list-style:none;\n  // margin:0;\n}\n  ul.nav.nav-tabs {\n    margin:0;\n  }\n  ul.nav.nav-tabs a:hover {\n    text-decoration:none;\n  }\n\n// TABBABLE\n// --------\n\n\n// COMMON STYLES\n// -------------\n\n// Clear any floats\n.tabbable {\n  .clearfix();\n}\n.tab-content {\n  display: table; // prevent content from running below tabs\n  width: 100%;\n  padding-top:5px;\n  background:darken(@pl-base,2);\n\n  p {\n    padding-left:10px;\n  }\n\n}\n\n\n// Remove border on bottom, left, right\n.tabs-below .nav-tabs,\n.tabs-right .nav-tabs,\n.tabs-left .nav-tabs {\n  border-bottom: 0;\n}\n\n// Show/hide tabbable areas\n.tab-content > .tab-pane,\n.pill-content > .pill-pane {\n  display: none;\n}\n.tab-content > .active,\n.pill-content > .active {\n  display: block;\n}\n\n\n// BOTTOM\n// ------\n\n.tabs-below .nav-tabs {\n  border-top: 1px solid darken(@pl-base, 6);\n}\n.tabs-below .nav-tabs > li {\n  margin-top: -1px;\n  margin-bottom: 0;\n}\n.tabs-below .nav-tabs > li > a {\n  .border-radius(0 0 4px 4px);\n  &:hover {\n    border-bottom-color: transparent;\n    border-top-color: darken(@pl-base, 6);\n  }\n}\n.tabs-below .nav-tabs .active > a,\n.tabs-below .nav-tabs .active > a:hover {\n  border-color: transparent darken(@pl-base, 6);\n}\n\n// LEFT & RIGHT\n// ------------\n\n// Common styles\n.tabs-left .nav-tabs > li,\n.tabs-right .nav-tabs > li {\n  float: none;\n}\n.tabs-left .nav-tabs > li > a,\n.tabs-right .nav-tabs > li > a {\n  min-width: 74px;\n  margin-right: 0;\n  margin-bottom: 3px;\n}\n\n// Tabs on the left\n.tabs-left .nav-tabs {\n  float: left;\n  margin-right: 19px;\n  border-right: 1px solid darken(@pl-base, 6);\n}\n.tabs-left .nav-tabs > li > a {\n  margin-right: -1px;\n  .border-radius(4px 0 0 4px);\n}\n.tabs-left .nav-tabs > li > a:hover {\n  border-color: darken(@pl-base, 6);\n}\n.tabs-left .nav-tabs .active > a,\n.tabs-left .nav-tabs .active > a:hover {\n  border-color: darken(@pl-base, 6);\n  *border-right-color: darken(@pl-base, 6);\n}\n\n// Tabs on the right\n.tabs-right .nav-tabs {\n  float: right;\n  margin-left: 19px;\n  border-left: 1px solid darken(@pl-base, 6);\n}\n.tabs-right .nav-tabs > li > a {\n  margin-left: -1px;\n  .border-radius(0 4px 4px 0);\n}\n.tabs-right .nav-tabs > li > a:hover {\n  border-color: darken(@pl-base, 6);\n}\n.tabs-right .nav-tabs .active > a,\n.tabs-right .nav-tabs .active > a:hover {\n  border-color: darken(@pl-base, 6);\n  *border-left-color: darken(@pl-base, 6);\n}\n//\n// Modals\n// --------------------------------------------------\n\n// Background\n.modal-backdrop {\n  position: fixed;\n  top: 0;\n  right: 0;\n  bottom: 0;\n  left: 0;\n  z-index: @zindexModalBackdrop;\n  background-color: @black;\n  // Fade for backdrop\n  &.fade { opacity: 0; }\n}\n\n.modal-backdrop,\n.modal-backdrop.fade.in {\n  .opacity(80);\n}\n\n// Base modal\n.modal {\n	position: fixed;\n	top: 10%;\n	left: 50%;\n	z-index: @zindexModal;\n	width: 560px;\n	margin-left: -280px;\n	background-color: @white;\n	border: 1px solid #999;\n	border: 1px solid rgba(0,0,0,.3);\n	*border: 1px solid #999; /* IE6-7 */\n	.border-radius(6px);\n	.box-shadow(0 3px 7px rgba(0,0,0,0.3));\n	.background-clip(padding-box);\n	// Remove focus outline from opened modal\n	outline: none;\n	font-family: @defaultFont;\n	font-weight: normal;\n	color:#777;\n	&.modal-large{\n		width: 700px;\n		margin-left: -350px\n	}\n	&.fade {\n		.transition(e(\'opacity .1s linear, top .1s ease-out\'));\n		top: -25%;\n	}\n	&.fade.in { top: 10%; }\n\n	h1, h2, h3, h4, h5, h6{\n		font-weight: bold;\n		color:#000;\n		font-family: @defaultFont;\n	}\n	iframe{\n		width: 100%;\n		min-height: 400px;\n	}\n}\n.modal-header {\n  padding: 9px 15px;\n  border-bottom: 1px solid #eee;\n  // Close icon\n  .close { margin-top: 2px; }\n  // Heading\n  h3 {\n\n    margin: 0;\n    line-height: 30px;\n  }\n}\n\n// Body (where all modal content resides)\n.modal-body {\n  position: relative;\n  overflow-y: auto;\n  max-height: 400px;\n  padding: @baseLineHeight;\n}\n// Remove bottom margin if need be\n.modal-form {\n  margin-bottom: 0;\n}\n\n// Footer (for actions)\n.modal-footer {\n  padding: 14px 15px 15px;\n  margin-bottom: 0;\n  text-align: right; // right align buttons\n  background-color: #f5f5f5;\n  border-top: 1px solid #ddd;\n  .border-radius(0 0 6px 6px);\n  .box-shadow(inset 0 1px 0 @white);\n  .clearfix(); // clear it in case folks use .pull-* classes on buttons\n\n  // Properly space out buttons\n  .btn + .btn {\n    margin-left: 5px;\n    margin-bottom: 0; // account for input[type=\"submit\"] which gets the bottom margin like all other inputs\n  }\n  // but override that for button groups\n  .btn-group .btn + .btn {\n    margin-left: -1px;\n  }\n  // and override it for block buttons as well\n  .btn-block + .btn-block {\n    margin-left: 0;\n  }\n}// Make wrapper ul behave like the grid\nbody .pl-thumbnails,\nbody .gallery{\n	margin-left: -@gridGutterWidth;\n	list-style: none;\n\n	.clearfix();\n	> li{\n	  float: left; // Explicity set the float since we don\'t require .span* classes\n	  margin-bottom: @baseLineHeight;\n	  margin-left: @gridGutterWidth;\n	}\n	\n}\n\nbody .pl-thumbnails .thumbnail,\nbody .gallery .gallery-icon a,\n.pl-thumbnail {\n	display: block;\n	padding: 4px;\n	line-height: @baseLineHeight;\n	border: 1px solid @baseContrast;\n	.border-radius(4px);\n	.box-shadow(0 1px 3px rgba(0,0,0,.055));\n	.transition(all .2s ease-in-out);\n	&[href]:hover{\n		border-color: @linkColor;\n		.box-shadow(0 1px 4px rgba(0,105,214,.25));\n	}\n	> img {\n	  display: block;\n	  max-width: 100%;\n	  margin-left: auto;\n	  margin-right: auto;\n	}\n	.caption, .gallery-caption {\n	  padding: 9px;\n	  color: @textColorAlt;\n	}\n}\n\n\n#gallery-1 img {\n	border: none!important;\n}\n.gallery-icon{\n	display: inline-block;\n}\n// Fluid rows have no left margin\n.row-fluid .thumbnails {\n  margin-left: 0;\n}\n// COMPONENT ANIMATIONS\n// --------------------\n\n.fade {\n  .transition(opacity .15s linear);\n  opacity: 0;\n  &.in {\n    opacity: 1;\n  }\n}\n\n.collapse {\n