INSERT INTO `wp_options` VALUES (534706,'_transient_plapi_draft_core_raw','a:2:{s:8:\"sections\";s:39539:\".section-postauthor{\n	margin-bottom: 2em;\n	text-align: left;\n	.copy-pad {\n		padding:  10px 15px;\n	}\n	.author-note{\n		.noteText();\n	}\n	.subtext{\n		font-size: 86%;\n		color: @textColorAlt;\n	}\n}\n.section-mediabox {\n	\n	.pl-section-pad{\n		padding: 0;\n	}\n	.mediabox-wrap{\n		min-height: 50px;\n	//	overflow: hidden;\n		.background-size(cover);\n		\n	}\n\n	img,\n	iframe,\n	object, \n	embed{\n		max-width: 100%;\n		max-height: 100%;\n	}\n	\n	\n	\n}.pl-sharebar-pad{\n	line-height: 20px;\n	padding: 10px;\n	text-align: center;\n}\n.pl-sharebar-text{\n	.noteText();\n}\n\n.meta-share{\n	padding-top: 5px;\n	iframe, object{\n		width: auto;\n	}\n}\n\n.fb-like{\n	margin-right: 20px;\n}\n\n.pin_wrap {\n	display: inline-block;\n}.popthumbs-wrap{\n	text-align: center;\n	.popthumb{\n		width: 100%; \n		max-width: 200px; \n		display: inline-block; \n		position: relative;\n		.expander{\n			position: absolute; \n			\n			border: 2px solid #fff; \n			background: rgba(0,0,0,.3);\n			box-shadow: 0 1px 2px rgba(0,0,0,.3);\n			.border-radius(100px);\n			font-size: 20px;\n			padding: 10px;\n			text-align: center;\n			top: -17px; \n			right: -17px;\n			height: 17px;\n			width: 17px;\n			color: #fff; \n			line-height: 17px;\n		}\n		background: #fff; \n		padding: 4px; \n		.border-radius(5px);\n		box-shadow: 0 1px 3px rgba(0,0,0,.3);\n		margin-bottom: 8px;\n		&:hover{\n			opacity: .8;\n		}\n		\n	}\n	.popthumb-text{\n		padding: 8px 0;\n		.popthumb-desc{\n			opacity: .75;\n		}\n	}\n}\n/* PAGELINES STYLES */\n .revslider-container{\n	width:100% !important;\n	position:relative;\n	padding:0;\n	max-height:480px !important;\n	overflow:hidden;\n 	.tp-thumbs {\n		padding:3px;\n	}\n\n	.text-center{\n		text-align: center;\n\n	}\n	/* Text Sizing*/\n	.slider-content{\n		font-family: @plHeaderFont;\n		text-shadow: 0 -1px 0 rgba(0,0,0,.7);\n		font-size: 34px;\n		line-height: 57px;\n		&.left-side,\n		&.right-side{\n			max-width: 400px;\n			text-align: left;\n		}\n		&.centered{\n			text-align: center;\n		}\n\n		h2{\n			font-size: inherit;\n			line-height: inherit;\n			.slider-text {\n				padding: 4px 0px;\n				background: rgba(0, 0, 0, 0.5);\n				box-shadow: 10px 0 0 rgba(0, 0, 0, 0.5), -10px 0 0 rgba(0, 0, 0, 0.5);\n				color: #fff;\n\n				margin-bottom: 1px;\n				background-position: right -53px;\n				.transition(all 0.3s linear);\n				margin-bottom: 15px;\n			}\n		}\n		a.slider-btn {\n			font-size: 13px;\n			line-height: 1.3em;\n			color: #eee;\n			background: rgba(0, 0, 0, 0.5);\n			left: -10px;\n			top: 10px;\n			position: relative;\n			padding: 10px 18px;\n			.transition(all 0.3s linear);\n			&:hover{\n				color:#fff;\n				background: @linkColor;\n			}\n		}\n		&.slider-black-text{\n			.slider-text{\n				background: rgba(255, 255, 255, 0.7);\n				box-shadow: 10px 0 0 rgba(255, 255, 255, 0.7), -10px 0 0 rgba(255, 255, 255, 0.7);\n				text-shadow: 0 1px 0 #fff;\n				color:#000;\n			}\n			a.slider-btn{\n				background: #fff;\n				color: #000;\n					text-shadow: 0 1px 0 #fff;\n				opacity: .6;\n				&:hover{\n					opacity: 1;\n				}\n			}\n		}\n	}\n\n\n\n\n}\n\n\n/* SLIDER STYLES */\n\n.header-shadow{\n	background:  url(\"@{plRoot}/sections/revslider/images/header-shadow.png\") no-repeat center top;\n	background-size: 100% auto;\n	width: 100%;\n	height: 30px;\n	position: absolute;\n	top: 0;\n	left: 0;\n	z-index: 27;\n}\n\n.tp-hide-revslider,\n.tp-caption.tp-hidden-caption{\n	visibility:hidden !important;\n	display:none !important;\n}\n\n.tp-caption {\n	z-index:1;\n	&.boxshadow{\n		box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.5);\n	}\n	&.noshadow {\n		text-shadow: none;\n	}\n	a {\n		.transition(all 0.2s ease-out);\n	}\n}\n\n.tp_inner_padding{\n	.box-sizing( border-box );\n	max-height:none !important;\n}\n\n\n/****************************************************************\n\n	-	SET THE ANIMATION EVEN MORE SMOOTHER ON ANDROID   -\n\n******************************************************************/\n.tp-simpleresponsive{\n	.slotholder *,\n	.tp-simpleresponsive img{\n		-webkit-transform: translateZ(0);\n		-webkit-backface-visibility: hidden;\n		-webkit-perspective: 1000;\n	}\n	caption,\n	.tp-caption {\n		opacity: 0;\n		position:absolute;\n		visibility: hidden;\n\n	}\n}\n\n\n/******************************\n	-	IE8 HACKS	-\n*******************************/\n.noFilterClass {\n	filter:none !important;\n}\n\n\n/******************************\n	-	SHADOWS		-\n******************************/\n.tp-bannershadow  {\n	position:absolute;\n\n	margin-left:auto;\n	margin-right:auto;\n	.user-select(none);\n\n}\n\n\n\n/********************************\n	-	FULLSCREEN VIDEO	-\n*********************************/\n.caption,\n.tp-caption{\n	&.fullscreenvideo {\n		left:0px;\n		top:0px;\n		position:absolute;\n		width:100%;\n		height:100%;\n		iframe	{ width:100% !important; height:100% !important;}\n	}\n}\n\n/************************\n	-	NAVIGATION	-\n*************************/\n\n\n.tpclear{clear:both;}\n\n.tp-bullets	{\n	z-index:1000;\n	position:absolute;\n	opacity: 1;\n	.transition(all 0.2s ease-out);\n	margin-bottom: 0px;\n	&.hidebullets	{\n		margin-bottom: -10px;\n		opacity: 0;\n	}\n	&.simplebullets.round .bullet{\n		cursor: pointer;\n		position: relative;\n		background: rgba(0,0,0,.6);\n		width: 8px;\n		height: 8px;\n		margin-right: 0px;\n		float: left;\n		margin-top: 0px;\n		margin-left: 5px;\n		.border-radius(8px);\n		border: 2px solid #fff;\n		opacity: .7;\n		.trans\n		&.last{\n			margin-right:3px;\n		}\n		&.selected,\n		&:hover{\n			opacity: 1;\n		}\n	}\n}\n\n/*************************************\n	-	TP ARROWS 	-\n**************************************/\n.tparrows{\n	opacity: 1;\n	.transition(opacity 0.2s ease-out);\n	&.hidearrows{\n		opacity: 0;\n	}\n}\n.tp-leftarrow,\n.tp-rightarrow{\n	cursor:pointer;\n	position:relative;\n	z-index:100;\n	width:40px;\n	height:40px;\n	&:hover{\n		background-position:bottom left;\n		opacity: .7;\n	}\n	&.default{\n\n	}\n}\n.tp-leftarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_left.png\") no-repeat top left;\n\n}\n.tp-rightarrow{\n	background:url(\"@{plRoot}/sections/revslider/assets/large_right.png\") no-repeat top left;\n}\n\n\n/****************************************************************************************************\n	-	TP THUMBS 	-\n*****************************************************************************************************\n\n - tp-thumbs & tp-mask Width is the width of the basic Thumb Container (500px basic settings)\n\n - .bullet width & height is the dimension of a simple Thumbnail (basic 100px x 50px)\n\n *****************************************************************************************************/\n\n\n.tp-bullets.tp-thumbs{\n	z-index:1000;\n	position:absolute;\n	padding:3px;\n	background-color:#fff;\n	width:500px;\n	height:50px; 			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n	margin-top:-50px;\n	.tp-mask{\n		width:500px;\n		height:50px;  			/* THE DIMENSIONS OF THE THUMB CONTAINER */\n		overflow:hidden;\n		position:relative;\n		.tp-thumbcontainer	{	width:5000px; position:absolute;}\n	}\n	.bullet{\n		width:100px;\n		height:50px;\n		cursor:pointer;\n		overflow:hidden;\n		background:none;\n		margin:0;\n		float:left;\n		opacity: 0.5;\n		.transition(all 0.2s ease-out);\n		&:hover,\n		&.selected{\n			opacity: 1;\n		}\n	}\n	img{\n		max-width:100%;\n	}\n}\n\n\n\n\n\n/************************************\n		-	TP BANNER TIMER		-\n*************************************/\n.tp-bannertimer	{\n	width:100%;\n	height:10px;\n	background:url(\"@{plRoot}/sections/revslider/assets/timer.png\");\n	position:absolute;\n	z-index:200;\n	&.tp-bottom{\n		bottom:0px !important;\n		height:5px;\n	}\n}\n\n@media only screen and (min-width: 480px) and (max-width: 767px) {\n	.responsive .tp-bullets.tp-thumbs{\n		width:300px !important;\n		height:30px !important;\n		.tp-mask{\n			width:300px !important; height:30px !important;\n		}\n		.bullet{\n			width:60px !important;height:30px !important;\n		}\n	}\n\n}\n\n   @media only screen and (min-width: 0px) and (max-width: 479px) {\n			.responsive .tp-bullets	{	display:none}\n			.responsive .tparrows	{	display:none;}\n}\n\n/*********************************************\n	-	BASIC SETTINGS FOR THE BANNER	-\n***********************************************/\n\n.tp-simpleresponsive img {\n	.user-select(none);\n}\n\n\n\n.tp-simpleresponsive {\n	a{	text-decoration:none;}\n	ul {\n		list-style:none;\n		padding:0;\n		margin:0;\n	}\n	 > ul >li{\n		list-stye:none;\n		position:absolute;\n		visibility:hidden;\n	}\n\n}\n\n/*  CAPTION SLIDELINK   **/\n.caption.slidelink a div,\n.tp-caption.slidelink a div {\n	width:10000px;\n	height:10000px;\n	background:url(\"@{plRoot}/sections/revslider/assets/coloredbg.png\") repeat;\n}\n\n.tp-loader 	{\n	background:url(\"@{plRoot}/sections/revslider/assets/loader.gif\") no-repeat 10px 10px;\n	background-color:#fff;\n	margin:-22px -22px;\n	top:50%;\n	left:50%;\n	z-index:10000;\n	position:absolute;\n	width:44px;\n	height:44px;\n	.border-radius(3px);\n}\n\n\n.tp-transparentimg {	content:\"url(@{plRoot}/sections/revslider/assets/transparent.png)\"}\n.tp-3d				{	-webkit-transform-style: preserve-3d;\n						 -webkit-transform-origin: 50% 50%;\n					}\n\n/***************** Bar graph ******************/\n\n\n.starbars-wrap{\n	padding: 1em;\n}\n.starbars{\n	text-align: left;\n	margin: 0;\n\n	li {\n		list-style: none!important;\n		&:last-child span {\n			margin-bottom: 0px;\n		}\n		.bar-wrap {\n			margin-bottom:13px;\n			.border-radius(300px);\n			background-color: @baseContrast;\n		    box-shadow:  inset 0 1px 2px rgba(0, 0, 0, 0.12);\n		    position: relative;\n\n		}\n		span {\n			height: 15px;\n			width: 0px;\n			display: block;\n			background-color: @linkColor;\n			.border-radius(300px);\n			overflow: visible!important;\n			position: relative;\n			strong {\n				opacity: 0;\n				color: #fff;\n				&:after{\n					right: 13px;\n				}\n			}\n		}\n		strong {\n			position: absolute;\n			right: -0px;\n			top: -25px;\n			opacity: .2;\n			padding: 4px 7px!important;\n			background-color: #333;\n			color: rgba(255,255,255,.8);\n			line-height: 12px!important;\n			font-size: 11px!important;\n			display: block;\n			font-family: @defaultFont;\n			.border-radius(2px);\n			&:after {\n				border-color: #333 transparent;\n			    border-style: solid;\n			    border-width: 5px 5px 0;\n			    bottom: -5px;\n			    content: \"\";\n			    display: block;\n			    right: 5px;\n			    position: absolute;\n			    width: 0;\n			}\n			&.full:after {\n				left: 15px;\n			}\n		}\n		p {\n			font-size: @fontSizeSmall;\n			margin: 0;\n			padding-bottom: 2px;\n		}\n	}\n}\n\n.ibox{\n	text-align: center;\n	.ibox-text h4{\n		margin-bottom: 5px;\n	}\n	.ibox-desc{\n\n		margin-bottom: 1em;\n		.ibox-link{\n			display: block;\n			margin-top: .5em;\n			i{\n				.transition(all 0.2s ease-in-out);\n\n			}\n			a:hover i{\n				margin-left: 6px;\n			}\n		}\n	}\n\n\n\n	.ibox-media{\n		text-align: center;\n		\n		.ibox-icon-border{\n			margin-bottom: @baseLineHeight/2;\n			display: inline-block;\n			text-align: center;\n			overflow: hidden;\n			.border-radius(500px);\n			img{\n				.border-radius(500px);\n			}\n			&.media-type-image {\n				.background-size(cover);\n				display: block;\n				margin-right: auto;\n				margin-left: auto;\n			}\n			&.media-type-icon {\n				.border-radius(500px);\n				width: 90px;\n				height: 90px;\n				line-height: 106px;\n\n				border: 1px solid transparent;\n				.transition(all 0.2s ease-in-out);\n				i{\n					font-size: 40px;\n					\n					display: inline-block;\n				}\n				&:hover{\n					background: @textColor;\n					i{\n						color: @pl-base;\n					}\n				}\n			}\n		}\n		\n	}\n	&.top-aligned{\n		.ibox-media{\n			max-width: 60%;\n			margin-right: auto;\n			margin-left: auto;\n			.media-type-image {\n			\n				height: 0; \n				padding: 50% 0;\n			}\n		}\n	}\n	&.left-aligned{\n		text-align: left;\n		.ibox-icon-border{\n			&.media-type-icon {\n\n				width: 70px;\n				height: 70px;\n				line-height: 85px;\n				i{\n					font-size: 35px;\n				}\n\n			}\n			&.media-type-image {\n				padding: auto;\n				width: 80px;\n				height: 80px;\n				img{\n					width: 80px;\n				}\n\n			}\n		}\n	}\n}\n\n\n\n.section-icallout {\n\n	text-align: center;\n	.icallout-head{\n		margin-bottom:12px;\n	}\n	.format-inline{\n		.icallout-head{\n			display: inline;\n			margin-right: 15px;\n			line-height: 1.1em;\n		}\n		.icallout-action{\n			vertical-align: top;\n		}\n	}\n	\n\n}\n.pl-watermark{\n	text-align: center; \n	.stack-element{\n		margin-bottom: 8px;\n	}\n	\n	.fb-like-box span{ display: block; line-height:0; padding:0; }\n	\n	.powered-by{\n		text-align: center;\n	\n		font-size: 10px;\n	}\n	\n	.the-watermark {\n		text-align: center;\n		padding: 5px;\n		a{\n			opacity: .7;\n			&:hover{\n				opacity: 1;\n			}\n			img{\n				max-width: 45px;\n			}\n		}\n	}\n}\n\n	\n\n\n.flipper-heading {\n	z-index: 100;\n	position: relative;\n	margin-bottom: 7px;\n	min-height: 23px;\n	line-height: 20px;\n	.flipper-title{\n		text-transform: uppercase; \n		font-weight: 600;\n		padding-top: 5px;\n	}\n	.flipper-next, \n	.flipper-prev {\n	    \n	    cursor: pointer;\n	    display: block;\n		text-align: center;\n	    margin-left: 5px;\n	    .transition(all 0.1s linear 0s);\n	    background-repeat: no-repeat!important;\n	    position: absolute;\n	    top: 0px;\n	    height: 20px;\n	    width: 20px;\n		right: 0px;\n	    .border-radius(1px);\n	\n	}\n\n	.flipper-prev {\n		right: 25px;\n	}\n\n	.flipper-prev:hover, \n	.flipper-next:hover  {\n		background-color: @linkColor;	\n		color: @pl-base;\n	}\n	\n}\n\n.section-flipper .pl-section-pad{\n	overflow: hidden;\n	position: relative;\n}\n.flipper-wrap {\n	position: relative;\n	margin-left: -10px;\n	margin-right: -10px;\n	.flipper {\n		opacity: 0;\n		max-height: 400px;\n		&.flipper-loaded{\n			max-height: none;\n		}\n	}\n	\n	.flipper-items {\n		overflow: hidden;\n		> li {\n			\n			position: relative;\n			display: block;\n			float: left;\n			margin: 0px 10px 0px 10px;\n			h3 {\n				position: relative;\n				top: 0px;\n				color: #fff;\n				margin-bottom: 10px;\n			}\n			img {\n				width: 100%;\n				display: block;\n				float: left;\n				margin-bottom: 0px;\n				\n			}\n		}\n		.flipper-item{\n			position: relative;\n			\n			.flipper-info-bg {\n				opacity: 0;\n				height: 100%;\n				width: 100%;\n				top: 0px;\n				left: 0px;\n				background-color: @linkColor;\n				position: absolute;\n				.transition(all 0.2s linear);\n			}\n\n			.flipper-info {\n				opacity: 0;\n				position: absolute;\n				display: block;\n				height: 100%;\n				width: 100%;\n				top: 0px;\n				left: 0px;\n				color: #fff;\n				.transition(all 0.2s linear);\n				.info-text {\n					background-color: rgba(0,0,0,.6);\n					padding: 9px 15px;\n					color: #fff;\n					font-size: 12px;\n					line-height: 1.3em;\n					margin: 5px;\n					display: inline-block;\n					.border-radius(3px);\n					.transition(all 0.1s linear);\n					&:hover {\n						opacity: 0.8;\n					}\n				}\n				.view,\n				.image, \n				.video {\n					position: absolute;\n					top: 0px;\n					left: 0px;\n					height: 100%;\n					width: 100%;\n					text-indent: -9999px;\n					color: #fff;\n					display: block;\n				}\n				span {\n					top: 0px;\n					left: 0px;\n					position: absolute;\n					display: block;\n					height: 42px;\n					width: 42px;\n					background-repeat: no-repeat;\n					background-position: center;\n					opacity: 0.8;\n					.transition(all 0.2s linear);\n				}\n				.pl-center {\n					.transition(all 0.2s linear);\n					text-align: center;\n					margin-top: 60px;\n					p {\n						padding-bottom: 0px;	\n					}\n				}\n			}\n			&:hover {\n\n				.flipper-info {\n					opacity: 1;\n					.pl-center {\n						padding-top: 20px;\n					}\n				}\n				.flipper-info-bg {\n					opacity: 0.9;\n					filter: alpha(opacity=90);\n				}\n			}\n		}\n\n		.flipper-meta {\n			padding-top: 10px;\n			text-align: left;\n			.flipper-post-title{\n				margin: 0;\n			}\n			.flipper-metabar{\n				opacity: .6;\n				font-size: @fontSizeSmall;\n			}\n		}\n	}\n}\n\n\n\n\n\n\n/* Jumbotrons\n-------------------------------------------------- */\n.masthead {\n\n    position: relative;\n    margin-bottom: @baseLineHeight * 3;\n\n	.inner{\n		padding-top: @baseLineHeight * 2;\n	}\n\n	&.with-splash{\n		.inner{\n			padding-top: @baseLineHeight;\n		}\n	}\n\n	h1,\n	p {\n	  text-align: center;\n	}\n\n	.masthead-title {\n		margin-bottom: 9px;\n		font-size: 81px;\n		letter-spacing: -1px;\n		line-height: 1;\n		margin-bottom: 18px;\n	}\n\n	.masthead-tag {\n		margin: 0;\n		margin-bottom: 18px;\n		margin-left: 5%;\n		margin-right: 5%;\n		font-size: 25px;\n		line-height: 1.3em;\n		opacity: .7;\n		font-weight: 300;\n		strong{\n			font-weight: 500;\n		}\n	}\n\n	.btn-large {\n	  font-size: 20px;\n	  font-weight: normal;\n	  padding: 14px 24px;\n	  margin: 0 10px;\n	  .border-radius(6px);\n	}\n\n	.btn-large small {\n	  font-size: 14px;\n	}\n\n	.splash {\n		padding-top: @baseLineHeight / 2;\n		img {\n			display:block;\n			margin:0 auto;\n		}\n	}\n}\n\n.mastmeta{\n	color: @textColorAlt;\n}\n\n\n\n.subhead {\n\n    padding-bottom: 0;\n    margin-bottom: 9px;\n\n	h1 {\n  		font-size: 54px;\n	}\n}\n\n.mastlinks {\n    margin: 36px 0;\n}\n\n.quick-links {\n\n	min-height: 30px;\n    margin: 0;\n    padding: 5px 20px;\n    list-style: none;\n    text-align: center;\n    overflow: hidden;\n\n	&:first-child {\n	  min-height: 0;\n	}\n	li {\n	  display: inline;\n	  margin: 0 8px;\n	}\n}\n\n\n\n/* Responsive\n ************************/\n\n@media screen and (max-width: 980px){\n	.masthead .masthead-title {\n		font-size: 72px;\n	}\n}\n@media screen and (max-width: 768px){\n\n	.masthead {\n\n		.inner{\n			padding-top: @baseLineHeight;\n		}\n\n		.masthead-title {\n			font-size: 54px;\n		}\n\n		.masthead-tag{\n			margin-right: 0;\n			margin-left: 0;\n		}\n		.btn {\n			margin-bottom: 10px;\n		}\n	}\n\n}\n@media screen and (max-width:480px) {\n	.masthead {\n