ight-radius(3px);\n	left: 0;\n	box-shadow: 1px -1px 0 rgba(255,255,255,.2);\n}\n\n\n// Toolbox/Toolbar\n// --------------------------------------------------\n\n#PageLinesToolbox.pl-toolbox{\n	position: fixed;\n	bottom: 0;\n	color: #000;\n	background: #fff;\n	font-size: 13px;\n	line-height: 21px;\n	width: 100%;\n	z-index: @zindexEditor;\n	min-width: 760px;\n	font-family: @defaultFont;\n	h1, h2, h3, h4, h5, h6{\n		font-family: @defaultFont;\n		font-weight: normal;\n	}\n	font-weight: normal;\n	select, textarea, input{\n		font-size: 14px;\n		line-height: 1.5em;\n	}\n	button{font-weight: bold;}\n	.type-textarea{\n		font-size: 12px;\n		line-height: 1.5em;\n		min-height: 9em;\n		padding: .7em;\n	}\n	.resizer-handle{\n		position: absolute;\n		height: 4px;\n		top: -4px;\n		width: 100%;\n		cursor: row-resize;\n		&:hover,\n		&.resizing{\n			background: rgba(53, 234, 253, .5);\n		}\n	}\n	.toolbox-handle{\n\n		@media (max-width: 1164px) {\n			 .btn-toolbox .txt{\n					display: none;\n			}\n		}\n\n		text-align: left;\n		.buttonBackground(#eee, #ddd);\n		box-shadow: inset 1px 1px 0 rgba(255,255,255,.8), 0 1px 3px rgba(0,0,0,.2);\n		border-top: 1px solid #999;\n		border-bottom: 1px solid #999;\n		font-weight: bold;\n		text-shadow: 0 1px 0 #fff;\n		height: 28px;\n		line-height: 28px;\n		font-size: 13px;\n		padding: 0 20px 0 24px;\n\n		.user-select(none);\n\n\n		ul.controls{\n			float: left;\n			margin-left: 11px;\n			border-left: 1px solid #AAA;\n			border-right: 1px solid #fff;\n			position: relative;\n			&.not-btn{\n				border-left: 1px solid transparent;\n				border-right: 1px solid transparent;\n				overflow: hidden;\n\n				width: 150px;\n				height: 28px;\n				> li {\n					&.switch-btn{\n						right: 0;\n						top: -30px;\n						opacity: 0;\n						position: absolute;\n						.transition( ~\"top .2s, opacity .3s\" );\n						&.active{\n							top: 0;\n							opacity: 1;\n						}\n						> .btn-toolbox{\n							border-left: 1px solid transparent;\n							border-right: 1px solid tra