x;\n	.flipper-title{\n		text-transform: uppercase; \n		font-weight: 600;\n		padding-top: 5px;\n	}\n	.flipper-next, \n	.flipper-prev {\n	    \n	    cursor: pointer;\n	    display: block;\n		text-align: center;\n	    margin-left: 5px;\n	    .transition(all 0.1s linear 0s);\n	    background-repeat: no-repeat!important;\n	    position: absolute;\n	    top: 0px;\n	    height: 20px;\n	    width: 20px;\n		right: 0px;\n	    .border-radius(1px);\n	\n	}\n\n	.flipper-prev {\n		right: 25px;\n	}\n\n	.flipper-prev:hover, \n	.flipper-next:hover  {\n		background-color: @linkColor;	\n		color: @pl-base;\n	}\n	\n}\n\n.section-flipper .pl-section-pad{\n	overflow: hidden;\n	position: relative;\n}\n.flipper-wrap {\n	position: relative;\n	margin-left: -10px;\n	margin-right: -10px;\n	.flipper {\n		opacity: 0;\n		max-height: 400px;\n		&.flipper-loaded{\n			max-height: none;\n		}\n	}\n	\n	.flipper-items {\n		overflow: hidden;\n		> li {\n			\n			position: relative;\n			display: block;\n			float: left;\n			margin: 0px 10px 0px 10px;\n			h3 {\n				position: relative;\n				top: 0px;\n				color: #fff;\n				margin-bottom: 10px;\n			}\n			img {\n				width: 100%;\n				display: block;\n				float: left;\n				margin-bottom: 0px;\n				\n			}\n		}\n		.flipper-item{\n			position: relative;\n			\n			.flipper-info-bg {\n				opacity: 0;\n				height: 100%;\n				width: 100%;\n				top: 0px;\n				left: 0px;\n				background-color: @linkColor;\n				position: absolute;\n				.transition(all 0.2s linear);\n			}\n\n			.flipper-info {\n				opacity: 0;\n				position: absolute;\n				display: block;\n				height: 100%;\n				width: 100%;\n				top: 0px;\n				left: 0px;\n				color: #fff;\n				.transition(all 0.2s linear);\n				.info-text {\n					background-color: rgba(0,0,0,.6);\n					padding: 9px 15px;\n					color: #fff;\n					font-size: 12px;\n					line-height: 1.3em;\n					margin: 5px;\n					display: inline-block;\n					.border-radius(3px);\n					.transition(all 0.1s linear);\n					&:hover {\n						opacity: 0.8;\n					}\n	